definition:
{
    "StartAt": "Start BFE Source Compaction schedule",
    "States": {
        "Start BFE Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RAW_CRAWLER",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_s3_bfe"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION BFE CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "Next": "SR_COMPACTION_BFE"
        },
        "SR_COMPACTION_BFE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SR_COMPACTION_BFE",
                    "StringEquals": "Y",
                    "Next": "SKIP SR_COMPACTION_BFE"
                }
            ],
            "Default": "LOAD SR_COMPACTION_BFE"
        },
        "LOAD SR_COMPACTION_BFE": {
            "Type": "Task",
            "ResultPath": "$.COMPACTION_BFE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "BFE",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_bfe_invoice_time_tracker_detail": "N",
                        "job_lnd_mrr_full_refresh_bfe_invoice_line_details": "N",
                        "job_lnd_mrr_full_refresh_bfe_invoice": "N",
                        "job_lnd_mrr_full_refresh_bfe_invoice_line_item": "N",
                        "job_lnd_mrr_full_refresh_bfe_bfe_rev_rec": "N",
                        "job_lnd_mrr_full_refresh_bfe_contract": "N",
                        "job_lnd_mrr_full_refresh_bfe_bfe_invoice_schedule": "N",
                        "job_lnd_mrr_full_refresh_bfe_asset": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:SR_COMPACTION_BFE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_BFE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP SR_COMPACTION_BFE"
        },
        "SNS_BFE_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Step Function for BFE_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP SR_COMPACTION_BFE": {
            "Type": "Pass",
            "Next": "CI_COMPACTION_BFE"
        },
        "CI_COMPACTION_BFE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CI_COMPACTION_BFE",
                    "StringEquals": "Y",
                    "Next": "SKIP CI_COMPACTION_BFE"
                }
            ],
            "Default": "LOAD CI_COMPACTION_BFE"
        },
        "LOAD CI_COMPACTION_BFE": {
            "Type": "Task",
            "ResultPath": "$.COMPACTION_BFE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "BFE",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_bfe_address_detail": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:CI_COMPACTION_BFE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_BFE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP CI_COMPACTION_BFE"
        },
        "SKIP CI_COMPACTION_BFE": {
            "Type": "Pass",
            "Next": "NONSS_JAPAN_FACTORING_COMPACTION_BFE"
        },
        "NONSS_JAPAN_FACTORING_COMPACTION_BFE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.NONSS_JAPAN_FACTORING_COMPACTION_BFE",
                    "StringEquals": "Y",
                    "Next": "SKIP NONSS_JAPAN_FACTORING_COMPACTION_BFE"
                }
            ],
            "Default": "LOAD NONSS_JAPAN_FACTORING_COMPACTION_BFE"
        },
        "LOAD NONSS_JAPAN_FACTORING_COMPACTION_BFE": {
            "Type": "Task",
            "ResultPath": "$.COMPACTION_BFE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "BFE",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_bfe_invoice_full": "N",
                        "job_lnd_mrr_full_refresh_bfe_invoice_line_item_full": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:NONSS_JAPAN_FACTORING_COMPACTION_BFE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_BFE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP NONSS_JAPAN_FACTORING_COMPACTION_BFE"
        },
        "SKIP NONSS_JAPAN_FACTORING_COMPACTION_BFE": {
            "Type": "Pass",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MIRROR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_bfe"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "ResultPath": "$.MRR_CRAWLER",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.COMPACTION_BFE.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "BFE"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "InputPath": "$",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_BFE_SCHEDULE_SUCCESS"
        },
        "SNS_BFE_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for BFE_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
