{
    "StartAt": "Start BF_EDHR_CS Source Compaction schedule",
    "States": {
        "Start BF_EDHR_CS Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RAW_CRAWLER",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_bf_edhr_cs"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION BF_EDHR_CS CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "Next": "IB_INTEL_COMPACTION_BF_EDHR_CS"
        },
        "IB_INTEL_COMPACTION_BF_EDHR_CS": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_BF_EDHR_CS",
                    "StringEquals": "Y",
                    "Next": "SKIP IB_INTEL_COMPACTION_BF_EDHR_CS"
                }
            ],
            "Default": "LOAD IB_INTEL_COMPACTION_BF_EDHR_CS"
        },
        "LOAD IB_INTEL_COMPACTION_BF_EDHR_CS": {
            "Type": "Task",
            "ResultPath": "$.BF_EDHR_CS_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "BF_EDHR_CS",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_consumed_part": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_uda_unit": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_uda_part": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_uda_lot": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_part": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_unit": "N",
                        "job_lnd_mrr_incr_refresh_ov_bf_edhr_cs_lot": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_BF_EDHR_CS"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_BF_EDHR_CS_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP IB_INTEL_COMPACTION_BF_EDHR_CS"
        },
        "SNS_BF_EDHR_CS_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Step Function for BF_EDHR_CS_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP IB_INTEL_COMPACTION_BF_EDHR_CS": {
            "Type": "Pass",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MIRROR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_bf_edhr_cs"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "ResultPath": "$.MRR_CRAWLER",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.BF_EDHR_CS_COMPACTION.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "BF_EDHR_CS",
                    "--max_threads": "5"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "InputPath": "$",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_BF_EDHR_CS_SCHEDULE_SUCCESS"
        },
        "SNS_BF_EDHR_CS_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for BF_EDHR_CS_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
