{
    "StartAt": "START MWS COMPACTION JOBS",
    "States": {
        "START MWS COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "MWS_PDMCOG_GPRSDATA_TO_PART",
                    "States": {
                        "MWS_PDMCOG_GPRSDATA_TO_PART": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_mws_prd_mwe_gprsdatpart_perm_s",
                                    "StringEquals": "Y",
                                    "Next": "PASS MWS_PDMCOG_GPRSDATA_TO_PART"
                                }
                            ],
                            "Default": "LOAD MWS_PDMCOG_GPRSDATA_TO_PART"
                        },
                        "LOAD MWS_PDMCOG_GPRSDATA_TO_PART": {
                            "Type": "Task",
                            "ResultPath": "$.Job1",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_full_refresh_mws_prd_mwe_gprsdatpart_perm_s",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MWS_PDMCOG_GPRSDATA_TO_PART_FAILED"
                                }
                            ],
                            "Next": "PASS MWS_PDMCOG_GPRSDATA_TO_PART"
                        },
                        "SNS_MWS_PDMCOG_GPRSDATA_TO_PART_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for MWS_PDMCOG_GPRSDATA_TO_PART Failed!"
                            },
                            "Next": "MWS_PDMCOG_GPRSDATA_TO_PART_ACTIVITY"
                        },
                        "MWS_PDMCOG_GPRSDATA_TO_PART_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_MWS_PRD_MWE_GPRSDATPART_PERM_S_ACTIVITY",
                            "End": true
                        },
                        "PASS MWS_PDMCOG_GPRSDATA_TO_PART": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S",
                    "States": {
                        "MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_mws_prd_mwe_gprspartchngup_perm_s",
                                    "StringEquals": "Y",
                                    "Next": "PASS MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S"
                                }
                            ],
                            "Default": "LOAD MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S"
                        },
                        "LOAD MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "ResultPath": "$.Job2",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_full_refresh_mws_prd_mwe_gprspartchngup_perm_s",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S_FAILED"
                                }
                            ],
                            "Next": "PASS MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S"
                        },
                        "SNS_MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S Failed!"
                            },
                            "Next": "MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S_ACTIVITY"
                        },
                        "MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S_ACTIVITY",
                            "End": true
                        },
                        "PASS MWS_PRD_MWE_GPRSPARTCHNGUP_PERM_S": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MWS_COMPACTION_SUCCESS"
        },
        "SNS_MWS_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for CD_COMPACTION_MWS completed successfully!!"
                }
            },
            "End": true
        }
    }
}
