{
    "StartAt": "START PARTS_FILES COMPACTION JOBS",
    "States": {
        "START PARTS_FILES COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$.SKIP_LAMBDA",
            "Next": "PARTS_FILES_LUT_EU_GEO_DATA"
        },
        "PARTS_FILES_LUT_EU_GEO_DATA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_parts_files_lut_eu_geo_data",
                    "StringEquals": "Y",
                    "Next": "PASS PARTS_FILES_LUT_EU_GEO_DATA"
                }
            ],
            "Default": "LOAD PARTS_FILES_LUT_EU_GEO_DATA"
        },
        "LOAD PARTS_FILES_LUT_EU_GEO_DATA": {
            "Type": "Task",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "ResultPath": "$.PARTS_FILES_LUT_EU_GEO_DATA",
            "Parameters": {
                "JobName": "job_lnd_mrr_full_refresh_parts_files_lut_eu_geo_data",
                "Arguments": {
                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_PARTS_FILES_LUT_EU_GEO_DATA_FAILED"
                }
            ],
            "Next": "PASS PARTS_FILES_LUT_EU_GEO_DATA"
        },
        "SNS_PARTS_FILES_LUT_EU_GEO_DATA_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for PARTS_FILES_LUT_EU_GEO_DATA Failed!"
            },
            "Next": "PARTS_FILES_LUT_EU_GEO_DATA_ACTIVITY"
        },
        "PARTS_FILES_LUT_EU_GEO_DATA_ACTIVITY": {
            "Type": "Task",
            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_PARTS_FILES_LUT_EU_GEO_DATA_ACTIVITY",
            "End": false
        },
        "PASS PARTS_FILES_LUT_EU_GEO_DATA": {
            "Type": "Pass",
            "InputPath": "$.SKIP_LAMBDA",
            "Next": "SNS_PARTS_FILE_COMPACTION_SUCCESS"
        },
        "SNS_PARTS_FILE_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$.SKIP_LAMBDA",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for CD_COMPACTION_PARTS_FILESCD_COMPACTION_PARTS_FILES completed successfully!!"
                }
            },
            "End": true
        }
    }
}
