{
    "StartAt": "START SALESFORCE COMPACTION JOBS",
    "States": {
        "START SALESFORCE COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "SALESFORCE_ACCOUNT"
        },
        "SALESFORCE_ACCOUNT": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_salesforce_account",
                    "StringEquals": "Y",
                    "Next": "PASS SALESFORCE_ACCOUNT"
                }
            ],
            "Default": "LOAD SALESFORCE_ACCOUNT"
        },
        "LOAD SALESFORCE_ACCOUNT": {
            "Type": "Task",
            "ResultPath": "$.SALESFORCE_ACCOUNT",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_lnd_mrr_incr_refresh_ov_salesforce_account",
                "Arguments": {
                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_SALESFORCE_ACCOUNT_FAILED"
                }
            ],
            "Next": "PASS SALESFORCE_ACCOUNT"
        },
        "SNS_SALESFORCE_ACCOUNT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SNS_SALESFORCE_ACCOUNT Failed!"
            },
            "Next": "SALESFORCE_ACCOUNT_ACTIVITY"
        },
        "SALESFORCE_ACCOUNT_ACTIVITY": {
            "Type": "Task",
            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SALESFORCE_ACCOUNT_ACTIVITY",
            "End": false
        },
        "PASS SALESFORCE_ACCOUNT": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_SALESFORCE_COMPACTION_SUCCESS"
        },
        "SNS_SALESFORCE_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SALESFORCE_COMPACTION completed successfully!!"
                }
            },
            "End": true
        }
    }
}
