{
    "StartAt": "START SBOM COMPACTION JOBS",
    "States": {
        "START SBOM COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "SBOM_SOR_PART_PARENT_CHILD",
                    "States": {
                        "SBOM_SOR_PART_PARENT_CHILD": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_sbom_sbom_sor_part_parent_child",
                                    "StringEquals": "Y",
                                    "Next": "PASS SBOM_SOR_PART_PARENT_CHILD"
                                }
                            ],
                            "Default": "LOAD SBOM_SOR_PART_PARENT_CHILD"
                        },
                        "LOAD SBOM_SOR_PART_PARENT_CHILD": {
                            "Type": "Task",
                            "ResultPath": "$.SBOM1",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_full_refresh_sbom_sbom_sor_part_parent_child",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_SBOM_SOR_PART_PARENT_CHILD_FAILED"
                                }
                            ],
                            "Next": "PASS SBOM_SOR_PART_PARENT_CHILD"
                        },
                        "SNS_SBOM_SOR_PART_PARENT_CHILD_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SBOM_SOR_PART_PARENT_CHILD Failed!"
                            },
                            "Next": "SBOM_SOR_PART_PARENT_CHILD_ACTIVITY"
                        },
                        "SBOM_SOR_PART_PARENT_CHILD_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SBOM_SBOM_SOR_PART_PARENT_CHILD_ACTIVITY",
                            "End": true
                        },
                        "PASS SBOM_SOR_PART_PARENT_CHILD": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "SBOM_TIX_SVC_PRT_ICV_S",
                    "States": {
                        "SBOM_TIX_SVC_PRT_ICV_S": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_sbom_tix_svc_prt_icv_s",
                                    "StringEquals": "Y",
                                    "Next": "PASS SBOM_TIX_SVC_PRT_ICV_S"
                                }
                            ],
                            "Default": "LOAD SBOM_TIX_SVC_PRT_ICV_S"
                        },
                        "LOAD SBOM_TIX_SVC_PRT_ICV_S": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "ResultPath": "$.SBOM2",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_full_refresh_sbom_tix_svc_prt_icv_s",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_SBOM_TIX_SVC_PRT_ICV_S_FAILED"
                                }
                            ],
                            "Next": "PASS SBOM_TIX_SVC_PRT_ICV_S"
                        },
                        "SNS_SBOM_TIX_SVC_PRT_ICV_S_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SBOM_TIX_SVC_PRT_ICV_S Failed!"
                            },
                            "Next": "SBOM_TIX_SVC_PRT_ICV_S_ACTIVITY"
                        },
                        "SBOM_TIX_SVC_PRT_ICV_S_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SBOM_TIX_SVC_PRT_ICV_S_ACTIVITY",
                            "End": true
                        },
                        "PASS SBOM_TIX_SVC_PRT_ICV_S": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "SNS_SBOM_COMPACTION_SUCCESS"
        },
        "SNS_SBOM_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for CD_COMPACTION_SBOM completed successfully!!"
                }
            },
            "End": true
        }
    }
}
