{
    "StartAt": "START CD_ENTITY_SBOM SCHEDULE",
    "States": {
        "START CD_ENTITY_SBOM SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "SBOM_PRD_MAIN_REL_A"
        },
        "SBOM_PRD_MAIN_REL_A": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.PASS_FLAG.SBOM_PRD_MAIN_REL_A",
                    "StringEquals": "Y",
                    "Next": "PASS SBOM_PRD_MAIN_REL_A"
                }
            ],
            "Default": "LOAD SBOM_PRD_MAIN_REL_A"
        },
        "LOAD SBOM_PRD_MAIN_REL_A": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.SBOM_PRD_MAIN_REL_A",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_prd_main_rel_as_sbom()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_SBOM_PRD_MAIN_REL_A_FAILED"
                }
            ],
            "Next": "PASS SBOM_PRD_MAIN_REL_A"
        },
        "SNS_SBOM_PRD_MAIN_REL_A_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SF_Success",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SBOM_PRD_MAIN_REL_A Failed!"
            },
            "End": false
        },
        "PASS SBOM_PRD_MAIN_REL_A": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_CD_ENTITY_SBOM_SUCCESS"
        },
        "SNS_CD_ENTITY_SBOM_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for CD_ENTITY_SBOM completed successfully!!"
                }
            },
            "End": true
        }
    }
}
