{
    "StartAt": "START MWS SCHEDULE",
    "States": {
        "START MWS SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "MWS_PRD_MAIN_D",
                    "States": {
                        "MWS_PRD_MAIN_D": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.MWS_PRD_MAIN_D",
                                    "StringEquals": "Y",
                                    "Next": "SKIP MWS_PRD_MAIN_D"
                                }
                            ],
                            "Default": "LOAD MWS_PRD_MAIN_D"
                        },
                        "LOAD MWS_PRD_MAIN_D": {
                            "Type": "Task",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "call_rs_procedure",
                                "Arguments": {
                                    "--rs_proc_name": "hdl_services_sp.sp_prd_main_ds_mws()"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MWS_PRD_MAIN_D_FAILED"
                                }
                            ],
                            "Next": "SKIP MWS_PRD_MAIN_D"
                        },
                        "SNS_MWS_PRD_MAIN_D_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": "AWS GLUE SHELL JOB FOR MWS_PRD_MAIN_D FAILED!"
                            },
                            "Next": "MWS_PRD_MAIN_D_ACTIVITY"
                        },
                        "MWS_PRD_MAIN_D_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:MWS_PRD_MAIN_D_ACTIVITY",
                            "End": true
                        },
                        "SKIP MWS_PRD_MAIN_D": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "MWS_PRD_MAIN_REL_A",
                    "States": {
                        "MWS_PRD_MAIN_REL_A": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.MWS_PRD_MAIN_REL_A",
                                    "StringEquals": "Y",
                                    "Next": "SKIP MWS_PRD_MAIN_REL_A"
                                }
                            ],
                            "Default": "LOAD MWS_PRD_MAIN_REL_A"
                        },
                        "LOAD MWS_PRD_MAIN_REL_A": {
                            "Type": "Task",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "call_rs_procedure",
                                "Arguments": {
                                    "--rs_proc_name": "hdl_services_sp.sp_prd_main_rel_as_gprspartchangeup()"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MWS_PRD_MAIN_REL_A_FAILED"
                                }
                            ],
                            "Next": "SKIP MWS_PRD_MAIN_REL_A"
                        },
                        "SNS_MWS_PRD_MAIN_REL_A_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": "AWS GLUE SHELL JOB FOR MWS_PRD_MAIN_REL_A FAILED!"
                            },
                            "Next": "MWS_PRD_MAIN_REL_A_ACTIVITY"
                        },
                        "MWS_PRD_MAIN_REL_A_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:MWS_PRD_MAIN_REL_A_ACTIVITY",
                            "End": true
                        },
                        "SKIP MWS_PRD_MAIN_REL_A": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MWS_SUCCESS"
        },
        "SNS_MWS_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for MWS completed successfully!!"
                }
            },
            "End": true
        }
    }
}
