{
    "StartAt": "START SAM SCHEDULE",
    "States": {
        "START SAM SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "SAM_PRT_CUST_PERSN_A",
                    "States": {
                        "SAM_PRT_CUST_PERSN_A": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.SAM_PRT_CUST_PERSN_A",
                                    "StringEquals": "Y",
                                    "Next": "SKIP SAM_PRT_CUST_PERSN_A"
                                }
                            ],
                            "Default": "LOAD SAM_PRT_CUST_PERSN_A"
                        },
                        "LOAD SAM_PRT_CUST_PERSN_A": {
                            "Type": "Task",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "call_rs_procedure",
                                "Arguments": {
                                    "--rs_proc_name": "hdl_services_sp.sp_prt_cust_persn_as_sam()"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_SAM_PRT_CUST_PERSN_A_FAILED"
                                }
                            ],
                            "Next": "SKIP SAM_PRT_CUST_PERSN_A"
                        },
                        "SNS_SAM_PRT_CUST_PERSN_A_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " SAM_PRT_CUST_PERSN_A Failed!"
                            },
                            "Next": "SAM_PRT_CUST_PERSN_A_ACTIVITY"
                        },
                        "SAM_PRT_CUST_PERSN_A_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:SAM_PRT_CUST_PERSN_A_ACTIVITY",
                            "End": true
                        },
                        "SKIP SAM_PRT_CUST_PERSN_A": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "SAM_PRT_PERSN_D",
                    "States": {
                        "SAM_PRT_PERSN_D": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.SAM_PRT_PERSN_D",
                                    "StringEquals": "Y",
                                    "Next": "SKIP SAM_PRT_PERSN_D"
                                }
                            ],
                            "Default": "LOAD SAM_PRT_PERSN_D"
                        },
                        "LOAD SAM_PRT_PERSN_D": {
                            "Type": "Task",
                            "InputPath": "$",
                            "OutputPath": "$",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "call_rs_procedure",
                                "Arguments": {
                                    "--rs_proc_name": "hdl_services_sp.sp_prd_main_ds_sit()"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_SAM_PRT_PERSN_D_FAILED"
                                }
                            ],
                            "Next": "SKIP SAM_PRT_PERSN_D"
                        },
                        "SNS_SAM_PRT_PERSN_D_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " SAM_PRT_PERSN_D Failed!"
                            },
                            "Next": "SAM_PRT_PERSN_D_ACTIVITY"
                        },
                        "SAM_PRT_PERSN_D_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:SAM_PRT_PERSN_D_ACTIVITY",
                            "End": true
                        },
                        "SKIP SAM_PRT_PERSN_D": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_SAM_SUCCESS"
        },
        "SNS_SAM_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SAM completed successfully!!"
                }
            },
            "End": true
        }
    }
}
