{
  "StartAt": "REFRESH SPOTFIRE Data Integrity",
  "States": {
    "REFRESH SPOTFIRE Data Integrity": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$",
      "Parameters": {
        "REPORT_LOCATION": "/Service Data Analytics/Project-X CTS/Analysis/Data Integrity"
      },
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:odp-spotfire-refresh",
      "Retry": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "MaxAttempts": 3,
          "IntervalSeconds": 5,
          "BackoffRate": 2
        }
      ],
      "End": true,
      "Catch": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "SNS FAILED Data Integrity"
        }
      ]
    },
    "SNS FAILED Data Integrity": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
        "Message.$": "$.Cause",
        "Subject": "Spotfire refresh failed for Data Integrity"
      },
      "End": true
    }
  }
}
