definition:
{
    "StartAt": "Start FLAT_FILE Source Compaction schedule",
    "States": {
        "Start FLAT_FILE Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RAW_CRAWLER",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_s3_flat_file"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION FLAT_FILE CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "Next": "CD_COMPACTION_FLAT_FILE"
        },
        "CD_COMPACTION_FLAT_FILE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CD_COMPACTION_FLAT_FILE",
                    "StringEquals": "Y",
                    "Next": "SKIP CD_COMPACTION_FLAT_FILE"
                }
            ],
            "Default": "LOAD CD_COMPACTION_FLAT_FILE"
        },
        "LOAD CD_COMPACTION_FLAT_FILE": {
            "Type": "Task",
            "ResultPath": "$.CD_COMPACTION_FLAT_FILE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "FLAT_FILE",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_incr_refresh_no_ov_flat_file_eopl_eosl": "N",
                        "job_lnd_mrr_full_refresh_flat_file_cd_alternate_parts": "N",
                        "job_lnd_mrr_full_refresh_flat_file_hr3_emp_data": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:CD_COMPACTION_FLAT_FILE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_FLAT_FILE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP CD_COMPACTION_FLAT_FILE"
        },
        "SNS_FLAT_FILE_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS SF for FLAT_FILE_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP CD_COMPACTION_FLAT_FILE": {
            "Type": "Pass",
            "Next": "IB_INTEL_COMPACTION_FLAT_FILE"
        },
        "IB_INTEL_COMPACTION_FLAT_FILE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_FLAT_FILE",
                    "StringEquals": "Y",
                    "Next": "SKIP IB_INTEL_COMPACTION_FLAT_FILE"
                }
            ],
            "Default": "LOAD IB_INTEL_COMPACTION_FLAT_FILE"
        },
        "LOAD IB_INTEL_COMPACTION_FLAT_FILE": {
            "Type": "Task",
            "ResultPath": "$.IB_INTEL_COMPACTION_FLAT_FILE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "FLAT_FILE",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_flat_file_contract": "N",
                        "job_lnd_mrr_full_refresh_flat_file_inactive_asset": "N",
                        "job_lnd_mrr_full_refresh_flat_file_must_cost_center_lut": "N",
                        "job_lnd_mrr_full_refresh_flat_file_must_dfsf_group_lut": "N",
                        "job_lnd_mrr_full_refresh_flat_file_region_eopl_eosl_dt": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_FLAT_FILE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_FLAT_FILE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP IB_INTEL_COMPACTION_FLAT_FILE"
        },
        "SKIP IB_INTEL_COMPACTION_FLAT_FILE": {
            "Type": "Pass",
            "Next": "SR_COMPACTION_FLAT_FILE"
        },
        "SR_COMPACTION_FLAT_FILE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SR_COMPACTION_FLAT_FILE",
                    "StringEquals": "Y",
                    "Next": "SKIP SR_COMPACTION_FLAT_FILE"
                }
            ],
            "Default": "LOAD SR_COMPACTION_FLAT_FILE"
        },
        "LOAD SR_COMPACTION_FLAT_FILE": {
            "Type": "Task",
            "ResultPath": "$.SR_COMPACTION_FLAT_FILE",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "SR_COMPACTION_FLAT_FILE1": "N",
                        "SR_COMPACTION_FLAT_FILE2": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:SR_COMPACTION_FLAT_FILE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_FLAT_FILE_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP SR_COMPACTION_FLAT_FILE"
        },
        "SKIP SR_COMPACTION_FLAT_FILE": {
            "Type": "Pass",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MIRROR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_flat_file"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.CD_COMPACTION_FLAT_FILE.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "FLAT_FILE"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_FLAT_FILE_SCHEDULE_SUCCESS"
        },
        "SNS_FLAT_FILE_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for FLAT_FILE_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
