{
    "StartAt": "Start GLA Source Compaction schedule",
    "States": {
        "Start GLA Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RAW_CRAWLER",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_s3_gla"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:541574621075:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION GLA CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "CD_COMPACTION_GLA"
        },
        "CD_COMPACTION_GLA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CD_COMPACTION_GLA",
                    "StringEquals": "Y",
                    "Next": "SKIP CD_COMPACTION_GLA"
                }
            ],
            "Default": "LOAD CD_COMPACTION_GLA"
        },
        "LOAD CD_COMPACTION_GLA": {
            "Type": "Task",
            "ResultPath": "$.CD_COMPACTION_GLA",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "GLA",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_gla_geo_addr_d": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_cust_d": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_cust_dtl_dx": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_cust_typ_d": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_cust_ucm_comrcl_hier_d": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_cust_ucm_hier_d": "N",
                        "job_lnd_mrr_full_refresh_gla_prt_persn_d": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:541574621075:stateMachine:CD_COMPACTION_GLA"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLA_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP CD_COMPACTION_GLA"
        },
        "SNS_GLA_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Step Function for GLA_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP CD_COMPACTION_GLA": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "IB_INTEL_COMPACTION_GLA"
        },
        "IB_INTEL_COMPACTION_GLA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_GLA",
                    "StringEquals": "Y",
                    "Next": "SKIP IB_INTEL_COMPACTION_GLA"
                }
            ],
            "Default": "LOAD IB_INTEL_COMPACTION_GLA"
        },
        "LOAD IB_INTEL_COMPACTION_GLA": {
            "Type": "Task",
            "ResultPath": "$.IB_INTEL_COMPACTION_GLA",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "StateMachineArn": "arn:aws:states:us-east-1:541574621075:stateMachine:COMPACTION_TEST_SCHEDULE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLA_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP IB_INTEL_COMPACTION_GLA"
        },
        "SKIP IB_INTEL_COMPACTION_GLA": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "SR_COMPACTION_GLA"
        },
        "SR_COMPACTION_GLA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SR_COMPACTION_GLA",
                    "StringEquals": "Y",
                    "Next": "SKIP SR_COMPACTION_GLA"
                }
            ],
            "Default": "LOAD SR_COMPACTION_GLA"
        },
        "LOAD SR_COMPACTION_GLA": {
            "Type": "Task",
            "ResultPath": "$.SR_COMPACTION_GLA",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "StateMachineArn": "arn:aws:states:us-east-1:541574621075:stateMachine:COMPACTION_TEST_SCHEDULE"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLA_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP SR_COMPACTION_GLA"
        },
        "SKIP SR_COMPACTION_GLA": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MIRROR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_gla"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:541574621075:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.CD_COMPACTION_GLA.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "GLA"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_GLA_SCHEDULE_SUCCESS"
        },
        "SNS_GLA_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for GLA_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
