definition:
{
    "StartAt": "Start GLPROD Source Compaction schedule",
    "States": {
        "Start GLPROD Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RawCrawler",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_s3_glprod"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION GLPROD CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "Next": "CD_COMPACTION_GLPROD"
        },
        "CD_COMPACTION_GLPROD": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CD_COMPACTION_GLPROD",
                    "StringEquals": "Y",
                    "Next": "SKIP CD_COMPACTION_GLPROD"
                }
            ],
            "Default": "LOAD CD_COMPACTION_GLPROD"
        },
        "LOAD CD_COMPACTION_GLPROD": {
            "Type": "Task",
            "ResultPath": "$.GLPROD_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "CD_COMPACTION_GLPROD1": "N",
                        "CD_COMPACTION_GLPROD2": "N",
                        "CD_COMPACTION_GLPROD3": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:CD_COMPACTION_GLPROD"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLPROD_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP CD_COMPACTION_GLPROD"
        },
        "SNS_GLPROD_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS SF for GLPROD_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP CD_COMPACTION_GLPROD": {
            "Type": "Pass",
            "Next": "IB_INTEL_COMPACTION_GLPROD"
        },
        "IB_INTEL_COMPACTION_GLPROD": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_GLPROD",
                    "StringEquals": "Y",
                    "Next": "SKIP IB_INTEL_COMPACTION_GLPROD"
                }
            ],
            "Default": "LOAD IB_INTEL_COMPACTION_GLPROD"
        },
        "LOAD IB_INTEL_COMPACTION_GLPROD": {
            "Type": "Task",
            "ResultPath": "$.GLPROD_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "GLPROD",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_incr_refresh_ov_glprod_oe_order_headers_all": "N",
                        "job_lnd_mrr_incr_refresh_ov_glprod_fulfillment_set": "N",
                        "job_lnd_mrr_incr_refresh_ov_glprod_mtl_unit_transactions": "N",
                        "job_lnd_mrr_incr_refresh_ov_glprod_wsh_delivery_details": "N",
                        "job_lnd_mrr_incr_refresh_ov_glprod_mtl_material_transactions": "N",
                        "job_lnd_mrr_incr_refresh_ov_glprod_oe_order_lines_all": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_GLPROD"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLPROD_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP IB_INTEL_COMPACTION_GLPROD"
        },
        "SKIP IB_INTEL_COMPACTION_GLPROD": {
            "Type": "Pass",
            "Next": "SR_COMPACTION_GLPROD"
        },
        "SR_COMPACTION_GLPROD": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SR_COMPACTION_GLPROD",
                    "StringEquals": "Y",
                    "Next": "SKIP SR_COMPACTION_GLPROD"
                }
            ],
            "Default": "LOAD SR_COMPACTION_GLPROD"
        },
        "LOAD SR_COMPACTION_GLPROD": {
            "Type": "Task",
            "ResultPath": "$.GLPROD_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "SR_COMPACTION_GLPROD1": "N",
                        "SR_COMPACTION_GLPROD2": "N",
                        "SR_COMPACTION_GLPROD3": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:SR_COMPACTION_GLPROD"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLPROD_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP SR_COMPACTION_GLPROD"
        },
        "SKIP SR_COMPACTION_GLPROD": {
            "Type": "Pass",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MRR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_glprod"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.GLPROD_COMPACTION.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "GLPROD"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_GLPROD_SCHEDULE_SUCCESS"
        },
        "SNS_GLPROD_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for GLPROD_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
