{
    "StartAt": "START IB_INTEL_CDS_ERROR SCHEDULE",
    "States": {
        "START IB_INTEL_CDS_ERROR SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "CDS_IB_ERROR"
        },
        "CDS_IB_ERROR": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CDS_IB_ERROR",
                    "StringEquals": "Y",
                    "Next": "SKIP CDS_IB_ERROR"
                }
            ],
            "Default": "LOAD CDS_IB_ERROR"
        },
        "LOAD CDS_IB_ERROR": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.CDS_IB_ERROR",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_srv_asset_cds_ib_error_f()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CDS_IB_ERROR_FAILED"
                }
            ],
            "Next": "SKIP CDS_IB_ERROR"
        },
        "SNS_CDS_IB_ERROR_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR CDS_IB_ERROR FAILED!"
            },
            "End": false
        },
        "SKIP CDS_IB_ERROR": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_IB_INTEL_CDS_ERROR_SUCCESS"
        },
        "SNS_IB_INTEL_CDS_ERROR_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for IB_INTEL_CDS_ERROR completed successfully!!"
                }
            },
            "End": true
        }
    }
}
