{
    "StartAt": "START IB_INTEL_CDS_HISTORY SCHEDULE",
    "States": {
        "START IB_INTEL_CDS_HISTORY SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "DTL_F_HIST"
        },
        "DTL_F_HIST": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.DTL_F_HIST",
                    "StringEquals": "Y",
                    "Next": "SKIP DTL_F_HIST"
                }
            ],
            "Default": "LOAD DTL_F_HIST"
        },
        "LOAD DTL_F_HIST": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.DTL_F_HIST",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_srv_asset_cds_dtl_f_hist()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_DTL_F_HIST_FAILED"
                }
            ],
            "Next": "SKIP DTL_F_HIST"
        },
        "SNS_DTL_F_HIST_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR DTL_F_HIST FAILED!"
            },
            "End": false
        },
        "SKIP DTL_F_HIST": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_IB_INTEL_CDS_HISTORY_SUCCESS"
        },
        "SNS_IB_INTEL_CDS_HISTORY_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for IB_INTEL_CDS_HISTORY completed successfully!!"
                }
            },
            "End": true
        }
    }
}
