{
    "StartAt": "START IB_INTEL_CDS_HISTORY_MERGE SCHEDULE",
    "States": {
        "START IB_INTEL_CDS_HISTORY_MERGE SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "SNAP_HIST_MERGE"
        },
        "SNAP_HIST_MERGE": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SNAP_HIST_MERGE",
                    "StringEquals": "Y",
                    "Next": "SKIP SNAP_HIST_MERGE"
                }
            ],
            "Default": "LOAD SNAP_HIST_MERGE"
        },
        "LOAD SNAP_HIST_MERGE": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.SNAP_HIST_MERGE",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--wlm_count": "5",
                    "--rs_proc_name": "hdl_services_sp.sp_srv_asset_cds_snap_hist_merge('202006')"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_SNAP_HIST_MERGE_FAILED"
                }
            ],
            "Next": "SKIP SNAP_HIST_MERGE"
        },
        "SNS_SNAP_HIST_MERGE_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR SNAP_HIST_MERGE FAILED!"
            },
            "End": false
        },
        "SKIP SNAP_HIST_MERGE": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_IB_INTEL_CDS_HISTORY_MERGE_SUCCESS"
        },
        "SNS_IB_INTEL_CDS_HISTORY_MERGE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for IB_INTEL_CDS_HISTORY_MERGE completed successfully!!"
                }
            },
            "End": true
        }
    }
}
