{
    "StartAt": "START CONNECTIVITY COMPACTION JOBS",
    "States": {
        "START CONNECTIVITY COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "CONNECTIVITY_PSI_CODE_LIST"
        },
        "CONNECTIVITY_PSI_CODE_LIST": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.job_lnd_mrr_full_refresh_connectivity_psi_code_list",
                    "StringEquals": "Y",
                    "Next": "PASS CONNECTIVITY_PSI_CODE_LIST"
                }
            ],
            "Default": "LOAD CONNECTIVITY_PSI_CODE_LIST"
        },
        "LOAD CONNECTIVITY_PSI_CODE_LIST": {
            "Type": "Task",
            "ResultPath": "$.CONNECTIVITY_PSI_CODE_LIST",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_lnd_mrr_full_refresh_connectivity_psi_code_list",
                "Arguments": {
                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CONNECTIVITY_PSI_CODE_LIST_FAILED"
                }
            ],
            "Next": "PASS CONNECTIVITY_PSI_CODE_LIST"
        },
        "SNS_CONNECTIVITY_PSI_CODE_LIST_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SNS_CONNECTIVITY_PSI_CODE_LIST Failed!"
            },
            "Next": "CONNECTIVITY_PSI_CODE_LIST_ACTIVITY"
        },
        "CONNECTIVITY_PSI_CODE_LIST_ACTIVITY": {
            "Type": "Task",
            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_CONNECTIVITY_PSI_CODE_LIST_ACTIVITY",
            "End": false
        },
        "PASS CONNECTIVITY_PSI_CODE_LIST": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_CONNECTIVITY_COMPACTION_SUCCESS"
        },
        "SNS_CONNECTIVITY_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for CONNECTIVITY COMPACTION completed successfully!!"
                }
            },
            "End": true
        }
    }
}
