{
  "StartAt": "START SALESFORCE COMPACTION JOBS",
  "States": {
    "START SALESFORCE COMPACTION JOBS": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$",
      "Next": "GET LAMBDA"
    },
    "GET LAMBDA": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.INPUT.GET_LAMBDA",
          "StringEquals": "Y",
          "Next": "SKIP GET LAMBDA"
        }
      ],
      "Default": "EXECUTE GET LAMBDA"
    },
    "EXECUTE GET LAMBDA": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.INPUT.BATCH_NUMBER",
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
      "Next": "SKIP GET LAMBDA"
    },
    "SKIP GET LAMBDA": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$.SKIP_LAMBDA",
      "Next": "StartParallel"
    },
    "StartParallel": {
      "Type": "Parallel",
      "Next": "Completed",
      "ResultPath": "$.parallelResult",
      "Branches": [
        {
          "StartAt": "SALESFORCE_IB_ASSET__C",
          "States": {
            "SALESFORCE_IB_ASSET__C": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_salesforce_ib_asset__c",
                  "StringEquals": "Y",
                  "Next": "PASS SALESFORCE_IB_ASSET__C"
                }
              ],
              "Default": "LOAD SALESFORCE_IB_ASSET__C"
            },
            "LOAD SALESFORCE_IB_ASSET__C": {
              "Type": "Task",
              "ResultPath": "$.SALESFORCE_IB_ASSET__C",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_incr_refresh_ov_salesforce_ib_asset__c",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_SALESFORCE_IB_ASSET__C_FAILED"
                }
              ],
              "Next": "PASS SALESFORCE_IB_ASSET__C"
            },
            "SNS_SALESFORCE_IB_ASSET__C_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SNS_SALESFORCE_IB_ASSET__C Failed!"
              },
              "Next": "SALESFORCE_IB_ASSET__C_ACTIVITY"
            },
            "SALESFORCE_IB_ASSET__C_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SALESFORCE_IB_ASSET_C_ACTIVITY",
              "End": true
            },
            "PASS SALESFORCE_IB_ASSET__C": {
              "Type": "Pass",
              "End": true
            }
          }
        },
        {
          "StartAt": "SALESFORCE_IB_SOURCE__C",
          "States": {
            "SALESFORCE_IB_SOURCE__C": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_salesforce_ib_source__c",
                  "StringEquals": "Y",
                  "Next": "PASS SALESFORCE_IB_SOURCE__C"
                }
              ],
              "Default": "LOAD SALESFORCE_IB_SOURCE__C"
            },
            "LOAD SALESFORCE_IB_SOURCE__C": {
              "Type": "Task",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "ResultPath": "$.SALESFORCE_IB_SOURCE__C",
              "Parameters": {
                "JobName": "job_lnd_mrr_incr_refresh_ov_salesforce_ib_source__c",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_SALESFORCE_IB_SOURCE__C_FAILED"
                }
              ],
              "Next": "PASS SALESFORCE_IB_SOURCE__C"
            },
            "SNS_SALESFORCE_IB_SOURCE__C_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SALESFORCE_IB_SOURCE__C Failed!"
              },
              "Next": "SALESFORCE_IB_SOURCE__C_ACTIVITY"
            },
            "SALESFORCE_IB_SOURCE__C_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SALESFORCE_IB_ASSET_C_ACTIVITY",
              "End": true
            },
            "PASS SALESFORCE_IB_SOURCE__C": {
              "Type": "Pass",
              "End": true
            }
          }
        },
        {
          "StartAt": "SALESFORCE_USER",
          "States": {
            "SALESFORCE_USER": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_salesforce_user",
                  "StringEquals": "Y",
                  "Next": "PASS SALESFORCE_USER"
                }
              ],
              "Default": "LOAD SALESFORCE_USER"
            },
            "LOAD SALESFORCE_USER": {
              "Type": "Task",
              "ResultPath": "$.SALESFORCE_USER",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_incr_refresh_ov_salesforce_user",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_SALESFORCE_USER_FAILED"
                }
              ],
              "Next": "PASS SALESFORCE_USER"
            },
            "SNS_SALESFORCE_USER_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for SNS_SALESFORCE_USER Failed!"
              },
              "Next": "SALESFORCE_USER_ACTIVITY"
            },
            "SALESFORCE_USER_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_SALESFORCE_USER_ACTIVITY",
              "End": true
            },
            "PASS SALESFORCE_USER": {
              "Type": "Pass",
              "End": true
            }
          }
        }
      ]
    },
    "Completed": {
      "Type": "Pass",
      "InputPath": "$.SKIP_LAMBDA",
      "Next": "SNS_SALESFORCE_COMPACTION_SUCCESS"
    },
    "SNS_SALESFORCE_COMPACTION_SUCCESS": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.snsResult",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
        "Message": {
          "Subject": "AWS Step function for SALESFORCE COMPACTION completed successfully!!"
        }
      },
      "End": true
    }
  }
}
