{
    "StartAt": "StartParallel",
    "States": {
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "IB_INTEL_COMPACTION_SIT1",
                    "States": {
                        "IB_INTEL_COMPACTION_SIT1": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_SIT1",
                                    "StringEquals": "Y",
                                    "Next": "PASS IB_INTEL_COMPACTION_SIT1"
                                }
                            ],
                            "Default": "LOAD IB_INTEL_COMPACTION_SIT1"
                        },
                        "LOAD IB_INTEL_COMPACTION_SIT1": {
                            "Type": "Task",
                            "ResultPath": "$.IB_INTEL_COMPACTION_SIT1",
                            "Resource": "arn:aws:states:::states:startExecution.sync",
                            "Parameters": {
                                "Input": {
                                    "INPUT": {
                                        "GET_LAMBDA": "N",
                                        "SRC_NAME": "SIT",
                                        "BATCH_NUMBER": "",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_asset_emp": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_srv_regn": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_srv_regn_zip": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_cx_srv_rgn_mod": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_ctlg": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_entl_act_type": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_sched_cal": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_sched_cal_hrs": "N"
                                    }
                                },
                                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_SIT1"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_IB_INTEL_COMPACTION_SIT1_FAILED"
                                }
                            ],
                            "Next": "PASS IB_INTEL_COMPACTION_SIT1"
                        },
                        "SNS_IB_INTEL_COMPACTION_SIT1_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for IB_INTEL_COMPACTION_SIT1 Failed!"
                            },
                            "Next": "IB_INTEL_COMPACTION_SIT1_ACTIVITY"
                        },
                        "IB_INTEL_COMPACTION_SIT1_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_IB_INTEL_COMPACTION_SIT1_ACTIVITY",
                            "End": true
                        },
                        "PASS IB_INTEL_COMPACTION_SIT1": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "IB_INTEL_COMPACTION_SIT2",
                    "States": {
                        "IB_INTEL_COMPACTION_SIT2": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_SIT2",
                                    "StringEquals": "Y",
                                    "Next": "PASS IB_INTEL_COMPACTION_SIT2"
                                }
                            ],
                            "Default": "LOAD IB_INTEL_COMPACTION_SIT2"
                        },
                        "LOAD IB_INTEL_COMPACTION_SIT2": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::states:startExecution.sync",
                            "ResultPath": "$.IB_INTEL_COMPACTION_SIT2",
                            "Parameters": {
                                "Input": {
                                    "INPUT": {
                                        "GET_LAMBDA": "N",
                                        "SRC_NAME": "SIT",
                                        "BATCH_NUMBER": "",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_entlmnt": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_agree_item": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_asset": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_asset_x": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_asset_xm": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_doc_agree": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_entlmnt_item": "N",
                                        "job_lnd_mrr_incr_refresh_ov_sit_s_prod_int_xm": "N"
                                    }
                                },
                                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_SIT2"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_IB_INTEL_COMPACTION_SIT2_FAILED"
                                }
                            ],
                            "Next": "PASS IB_INTEL_COMPACTION_SIT2"
                        },
                        "SNS_IB_INTEL_COMPACTION_SIT2_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for IB_INTEL_COMPACTION_SIT2 Failed!"
                            },
                            "Next": "IB_INTEL_COMPACTION_SIT2_ACTIVITY"
                        },
                        "IB_INTEL_COMPACTION_SIT2_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_IB_INTEL_COMPACTION_SIT2_ACTIVITY",
                            "End": true
                        },
                        "PASS IB_INTEL_COMPACTION_SIT2": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_SIT_COMPACTION_SUCCESS"
        },
        "SNS_SIT_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for COMPACTION_SIT completed successfully!!"
                }
            },
            "End": true
        }
    }
}
