{
    "StartAt": "START IB_INTEL_ENTITY_DQ_FRAMEWORK SCHEDULE",
    "States": {
        "START IB_INTEL_ENTITY_DQ_FRAMEWORK SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "DQ_FRAMEWORK"
        },
        "DQ_FRAMEWORK": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.DQ_FRAMEWORK",
                    "StringEquals": "Y",
                    "Next": "SKIP DQ_FRAMEWORK"
                }
            ],
            "Default": "LOAD DQ_FRAMEWORK"
        },
        "LOAD DQ_FRAMEWORK": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.DQ_FRAMEWORK",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_cds_dq_data_load_validation"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_DQ_FRAMEWORK_FAILED"
                }
            ],
            "Next": "SKIP DQ_FRAMEWORK"
        },
        "SNS_DQ_FRAMEWORK_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR DQ_FRAMEWORK FAILED!"
            },
            "End": false
        },
        "SKIP DQ_FRAMEWORK": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_IB_INTEL_ENTITY_DQ_FRAMEWORK_SUCCESS"
        },
        "SNS_IB_INTEL_ENTITY_DQ_FRAMEWORK_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for IB_INTEL_ENTITY_DQ_FRAMEWORK completed successfully!!"
                }
            },
            "End": true
        }
    }
}
