{
  "StartAt": "REFRESH SPOTFIRE IB Upgrade",
  "States": {
    "REFRESH SPOTFIRE IB Upgrade": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$",
      "Parameters": {
        "REPORT_LOCATION": "/Service Data Analytics/Upgrades Dashboard/Analyses/IB Upgrade"
      },
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:odp-spotfire-refresh",
      "Retry": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "MaxAttempts": 3,
          "IntervalSeconds": 5,
          "BackoffRate": 2
        }
      ],
      "Next": "REFRESH SPOTFIRE Machine Utilization",
      "Catch": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "SNS FAILED IB Upgrade"
        }
      ]
    },
    "SNS FAILED IB Upgrade": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
        "Message.$": "$.Cause",
        "Subject": "Spotfire refresh failed for IB Upgrade"
      },
      "Next": "REFRESH SPOTFIRE Machine Utilization"
    },
    "REFRESH SPOTFIRE Machine Utilization": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$",
      "Parameters": {
        "REPORT_LOCATION": "/Service Data Analytics/Upgrades Dashboard/Analyses/Machine Utilization"
      },
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:odp-spotfire-refresh",
      "Retry": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "MaxAttempts": 3,
          "IntervalSeconds": 5,
          "BackoffRate": 2
        }
      ],
      "End": true,
      "Catch": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "SNS FAILED Machine Utilization"
        }
      ]
    },
    "SNS FAILED Machine Utilization": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
        "Message.$": "$.Cause",
        "Subject": "Spotfire refresh failed for Machine Utilization"
      },
      "End": true
    }
  }
}
