{
    "StartAt": "UPDATE SNAPSHOT LAMBDA",
    "States": {
        "UPDATE SNAPSHOT LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.CHECK SNAPSHOT LAMBDA",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "contigency_c"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_snapshot_lock",
            "Next": "CHECK SNAPSHOT LAMBDA"
        },
        "CHECK SNAPSHOT LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.CDS_SNAPSHOT_GLPROD_CONTINGENCY",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "contigency_c_check"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_snapshot_lock",
            "Catch": [
                {
                    "ErrorEquals": [
                        "ZeroDivisionError"
                    ],
                    "Next": "SKIP CDS_SNAPSHOT_GLPROD_CONTINGENCY"
                }
            ],
            "Next": "CDS_SNAPSHOT_GLPROD_CONTINGENCY"
        },
        "CDS_SNAPSHOT_GLPROD_CONTINGENCY": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CDS_SNAPSHOT_GLPROD_CONTINGENCY",
                    "StringEquals": "Y",
                    "Next": "SKIP CDS_SNAPSHOT_GLPROD_CONTINGENCY"
                }
            ],
            "Default": "LOAD CDS_SNAPSHOT_GLPROD_CONTINGENCY"
        },
        "LOAD CDS_SNAPSHOT_GLPROD_CONTINGENCY": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.CDS_SNAPSHOT_GLPROD_CONTINGENCY",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_backorders_cds_snapshot_f_glprod_contingency(0)"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CDS_SNAPSHOT_GLPROD_CONTINGENCY_FAILED"
                }
            ],
            "Next": "REFRESH SPOTFIRE COMBINED ORDERS"
        },
        "SNS_CDS_SNAPSHOT_GLPROD_CONTINGENCY_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR CDS_SNAPSHOT_GLPROD_CONTINGENCY FAILED!"
            },
            "End": false
        },
        "REFRESH SPOTFIRE COMBINED ORDERS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$",
            "Parameters": {
                "REPORT_LOCATION": "/Service Data Analytics/ODP Redshift/MAPP/Analysis/BO Segmentation-Combined Orders"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:odp-spotfire-refresh",
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 5,
                    "BackoffRate": 2
                }
            ],
            "Next": "REFRESH SPOTFIRE DEMAND ORDERS",
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS FAILED COMBINED ORDERS LAMBDA"
                }
            ]
        },
        "REFRESH SPOTFIRE DEMAND ORDERS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$",
            "Parameters": {
                "REPORT_LOCATION": "/Service Data Analytics/ODP Redshift/MAPP/Analysis/BO Segmentation-Customer Demand Orders"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:odp-spotfire-refresh",
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 5,
                    "BackoffRate": 2
                }
            ],
            "Next": "Completed",
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS FAILED DEMAND ORDERS LAMBDA"
                }
            ]
        },
        "SNS FAILED COMBINED ORDERS LAMBDA": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "Spotfire refresh failed for COMBINED ORDERS"
            },
            "Next": "REFRESH SPOTFIRE DEMAND ORDERS"
        },
        "SNS FAILED DEMAND ORDERS LAMBDA": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "Spotfire refresh failed for COMBINED ORDERS"
            },
            "Next": "Completed"
        },
        "SKIP CDS_SNAPSHOT_GLPROD_CONTINGENCY": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_CDS_SNAPSHOT_GLPROD_CONTINGENCY_SUCCESS"
        },
        "SNS_CDS_SNAPSHOT_GLPROD_CONTINGENCY_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SNS_CDS_SNAPSHOT_GLPROD_CONTINGENCY_SUCCESS completed successfully!!"
                }
            },
            "End": true
        }
    }
}
