{
    "StartAt": "MAPP_CONTINGENCY_BACKFILL",
    "States": {
        "MAPP_CONTINGENCY_BACKFILL": {
            "Type": "Pass",
            "Next": "GLPROD_CONTINGENCY_BACKFILL"
        },
        "GLPROD_CONTINGENCY_BACKFILL": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GLPROD_CONTINGENCY_BACKFILL",
                    "StringEquals": "Y",
                    "Next": "SKIP GLPROD_CONTINGENCY_BACKFILL"
                }
            ],
            "Default": "LOAD GLPROD_CONTINGENCY_BACKFILL"
        },
        "LOAD GLPROD_CONTINGENCY_BACKFILL": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.GLPROD_CONTINGENCY_BACKFILL",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_backorders_cds_snapshot_f_glprod_backfill(7)"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLPROD_CONTINGENCY_BACKFILL_FAILED"
                }
            ],
            "Next": "SKIP GLPROD_CONTINGENCY_BACKFILL"
        },
        "SNS_GLPROD_CONTINGENCY_BACKFILL_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FORGLPROD_CONTINGENCY_BACKFILL FAILED!"
            },
            "End": false
        },
        "SKIP GLPROD_CONTINGENCY_BACKFILL": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_GLPROD_CONTINGENCY_BACKFILL_SUCCESS"
        },
        "SNS_GLPROD_CONTINGENCY_BACKFILL_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SNS_GLPROD_CONTINGENCY_BACKFILL_SUCCESS completed successfully!!"
                }
            },
            "End": true
        }
    }
}
