{
    "StartAt": "MAPP_DAILY_ENTITY_MASTER",
    "States": {
        "MAPP_DAILY_ENTITY_MASTER": {
            "Type": "Pass",
            "Next": "MAPP_CD_SR_ENTITY"
        },
        "MAPP_CD_SR_ENTITY": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MAPP_ENTITY",
                    "StringEquals": "Y",
                    "Next": "PASS MAPP_CD_SR_ENTITY"
                }
            ],
            "Default": "LOAD MAPP_CD_SR_ENTITY"
        },
        "LOAD MAPP_CD_SR_ENTITY": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.MAPP_ENTITY",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_mapp_entity_wrapper()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_MAPP_ENTITY_FAILED"
                }
            ],
            "Next": "PASS MAPP_CD_SR_ENTITY"
        },
        "SNS_MAPP_ENTITY_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR MAPP_ENTITY FAILED!"
            },
            "End": false
        },
        "PASS MAPP_CD_SR_ENTITY": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MAPP_DAILY_ENTITY_SUCCESS"
        },
        "SNS_MAPP_DAILY_ENTITY_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for MAPP_DAILY_ENTITY completed successfully!!"
                }
            },
            "End": true
        }
    }
}
