{
    "StartAt": "MAPP_MASTER_CONTINGENCY_SCHEDULE",
    "States": {
        "MAPP_MASTER_CONTINGENCY_SCHEDULE": {
            "Type": "Pass",
            "Result": "$",
            "Next": "CHECK CONTINGENCY RUN"
        },
        "CHECK CONTINGENCY RUN": {
            "Type": "Task",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "skip_contingency_finished_flag"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule_update",
            "Catch": [
                {
                    "ErrorEquals": [
                        "ZeroDivisionError"
                    ],
                    "Next": "SNS_CONTINGENCY_CHECK_FAILED"
                }
            ],
            "Next": "UPDATE CONTINGENCY LAMBDA"
        },
        "UPDATE CONTINGENCY LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "contingency_finished_flag"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule_update",
            "Next": "UPDATE SNAPSHOT LAMBDA"
        },
        "UPDATE SNAPSHOT LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "contigency_null"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_snapshot_lock",
            "End": true
        },
        "FAIL_SCHEDULE": {
            "Type": "Fail",
            "Cause": "Invalid response.",
            "Error": "ErrorA"
        },
        "SNS_CONTINGENCY_CHECK_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "MAPP CONTINGENCY job cannot be executed as other MAPP jobs are running !"
            },
            "Next": "FAIL_SCHEDULE"
        }
    }
}
