{
    "StartAt": "MAPP_MASTER_ONHAND_SCHEDULE",
    "States": {
        "MAPP_MASTER_ONHAND_SCHEDULE": {
            "Type": "Pass",
            "Result": "$",
            "Next": "CHECK OTHER RUN"
        },
        "CHECK OTHER RUN": {
            "Type": "Task",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule",
            "Catch": [
                {
                    "ErrorEquals": [
                        "ZeroDivisionError"
                    ],
                    "Next": "SNS_ONHAND_CHECK_FAILED"
                }
            ],
            "Next": "UPDATE ONHAND LAMBDA"
        },
        "UPDATE ONHAND LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "onhand_finished_flag"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule_update",
            "End": true
        },
        "FAIL_SCHEDULE": {
            "Type": "Fail",
            "Cause": "Invalid response.",
            "Error": "ErrorA"
        },
        "SNS_ONHAND_CHECK_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "MAPP Onhand Intraday job cannot be executed as other MAPP jobs are running !"
            },
            "Next": "FAIL_SCHEDULE"
        }
    }
}
