{
    "StartAt": "CHECK CONTINGENCY",
    "States": {
        "CHECK CONTINGENCY": {
            "Type": "Task",
            "ResultPath": "$.SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS ",
            "Parameters": {
                "DDB_KEY_TO_UPDATE": "skip_contingency_finished_flag"
            },
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule_update",
            "Catch": [
                {
                    "ErrorEquals": [
                        "ZeroDivisionError"
                    ],
                    "Next": "SNS_SKIP_UNSOURCED_SNAPSHOT_INTRADAY"
                }
            ],
            "Next": "SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS"
        },
        "SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS",
                    "StringEquals": "Y",
                    "Next": "SKIP SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS"
                }
            ],
            "Default": "LOAD SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS"
        },
        "LOAD SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.BACKORDERS_CDS_SNAPSHOT_UNSRCD_FS('HOURLY')"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS_FAILED"
                }
            ],
            "Next": "SKIP SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS"
        },
        "SNS_SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS FAILED!"
            },
            "End": false
        },
        "SKIP SNAPSHOT_BACKORDERS_CDS_UNSRCD_FS": {
            "Type": "Pass",
            "Next": "SNAPSHOT_CDC_UPDATE_HOURLY"
        },
        "SNAPSHOT_CDC_UPDATE_HOURLY": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SNAPSHOT_CDC_UPDATE_HOURLY",
                    "StringEquals": "Y",
                    "Next": "SKIP SNAPSHOT_CDC_UPDATE_HOURLY"
                }
            ],
            "Default": "LOAD SNAPSHOT_CDC_UPDATE_HOURLY"
        },
        "LOAD SNAPSHOT_CDC_UPDATE_HOURLY": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.SNAPSHOT_CDC_UPDATE_HOURLY",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_cdc_update_hourly()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_SNAPSHOT_CDC_UPDATE_HOURLY_FAILED"
                }
            ],
            "Next": "SKIP SNAPSHOT_CDC_UPDATE_HOURLY"
        },
        "SNS_SNAPSHOT_CDC_UPDATE_HOURLY_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR SNAPSHOT_CDC_UPDATE_HOURLY FAILED!"
            },
            "End": false
        },
        "SKIP SNAPSHOT_CDC_UPDATE_HOURLY": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MAPP_UNSOURCED_SNAPSHOT_INTRADAY_SUCCESS"
        },
        "SNS_MAPP_UNSOURCED_SNAPSHOT_INTRADAY_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for MAPP_UNSOURCED_SNAPSHOT_INTRADAY completed successfully!!"
                }
            },
            "End": true
        },
        "SNS_SKIP_UNSOURCED_SNAPSHOT_INTRADAY": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "Skipping Unsourced Snapshot as Continengency is running!!"
                }
            },
            "End": true
        }
    }
}
