{
    "StartAt": "Start MUST Source Compaction schedule",
    "States": {
        "Start MUST Source Compaction schedule": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "RAW CRAWLER"
        },
        "RAW CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RAW_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  RAW CRAWLER"
                }
            ],
            "Default": "START RAW CRAWLER"
        },
        "START RAW CRAWLER": {
            "Type": "Task",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "ResultPath": "$.RAW_CRAWLER",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_lnd_s3_must"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  RAW CRAWLER"
        },
        "SNS_CRAWLER_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION MUST CRAWLER FAILED !"
            },
            "End": false
        },
        "SKIP  RAW CRAWLER": {
            "Type": "Pass",
            "Next": "CD_COMPACTION_MUST"
        },
        "CD_COMPACTION_MUST": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.CD_COMPACTION_MUST",
                    "StringEquals": "Y",
                    "Next": "SKIP CD_COMPACTION_MUST"
                }
            ],
            "Default": "LOAD CD_COMPACTION_MUST"
        },
        "LOAD CD_COMPACTION_MUST": {
            "Type": "Task",
            "ResultPath": "$.MUST_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "MUST",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_must_syst": "N",
                        "job_lnd_mrr_full_refresh_must_parts": "N",
                        "job_lnd_mrr_full_refresh_must_cust_acc": "N",
                        "job_lnd_mrr_full_refresh_must_fse": "N",
                        "job_lnd_mrr_full_refresh_must_site": "N",
                        "job_lnd_mrr_full_refresh_must_tab003uk": "N",
                        "job_lnd_mrr_full_refresh_must_conthrs": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:CD_COMPACTION_MUST"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_MUST_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP CD_COMPACTION_MUST"
        },
        "SNS_MUST_COMPACTION_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Step Function for MUST_COMPACTION Failed!"
            },
            "End": false
        },
        "SKIP CD_COMPACTION_MUST": {
            "Type": "Pass",
            "Next": "IB_INTEL_COMPACTION_MUST"
        },
        "IB_INTEL_COMPACTION_MUST": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.IB_INTEL_COMPACTION_MUST",
                    "StringEquals": "Y",
                    "Next": "SKIP IB_INTEL_COMPACTION_MUST"
                }
            ],
            "Default": "LOAD IB_INTEL_COMPACTION_MUST"
        },
        "LOAD IB_INTEL_COMPACTION_MUST": {
            "Type": "Task",
            "ResultPath": "$.MUST_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "GET_LAMBDA": "N",
                        "SRC_NAME": "MUST",
                        "BATCH_NUMBER": "",
                        "job_lnd_mrr_full_refresh_must_contract1": "N",
                        "job_lnd_mrr_full_refresh_must_dtlctrtyp": "N",
                        "job_lnd_mrr_full_refresh_must_contract2": "N",
                        "job_lnd_mrr_full_refresh_must_dtlctrdfs": "N",
                        "job_lnd_mrr_full_refresh_must_installed_prod1": "N",
                        "job_lnd_mrr_full_refresh_must_tabfonuk": "N",
                        "job_lnd_mrr_full_refresh_must_mastcont": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:IB_INTEL_COMPACTION_MUST"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_MUST_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP IB_INTEL_COMPACTION_MUST"
        },
        "SKIP IB_INTEL_COMPACTION_MUST": {
            "Type": "Pass",
            "Next": "SR_COMPACTION_MUST"
        },
        "SR_COMPACTION_MUST": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.SR_COMPACTION_MUST",
                    "StringEquals": "Y",
                    "Next": "SKIP SR_COMPACTION_MUST"
                }
            ],
            "Default": "LOAD SR_COMPACTION_MUST"
        },
        "LOAD SR_COMPACTION_MUST": {
            "Type": "Task",
            "ResultPath": "$.MUST_COMPACTION",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "INPUT": {
                        "SR_COMPACTION_MUST1": "N",
                        "SR_COMPACTION_MUST2": "N",
                        "SR_COMPACTION_MUST3": "N"
                    }
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:SR_COMPACTION_MUST"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_MUST_COMPACTION_FAILED"
                }
            ],
            "Next": "SKIP SR_COMPACTION_MUST"
        },
        "SKIP SR_COMPACTION_MUST": {
            "Type": "Pass",
            "Next": "Compaction Done"
        },
        "Compaction Done": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "MIRROR CRAWLER"
        },
        "MIRROR CRAWLER": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.MIRROR_CRAWLER",
                    "StringEquals": "Y",
                    "Next": "SKIP  MIRROR CRAWLER"
                }
            ],
            "Default": "START MIRROR CRAWLER"
        },
        "START MIRROR CRAWLER": {
            "Type": "Task",
            "ResultPath": "$.MIRROR_CRAWLER",
            "Resource": "arn:aws:states:::states:startExecution.sync",
            "Parameters": {
                "Input": {
                    "CRAWLER_NAME": "crw_compacted_mrr_s3_must"
                },
                "StateMachineArn": "arn:aws:states:us-east-1:245792935030:stateMachine:COMPACTION_CRAWLER"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_CRAWLER_FAILED"
                }
            ],
            "Next": "SKIP  MIRROR CRAWLER"
        },
        "SKIP  MIRROR CRAWLER": {
            "Type": "Pass",
            "Next": "Put Lambda"
        },
        "Put Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.PUT_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "Skip Put Lambda"
                }
            ],
            "Default": "Execute Put Lambda"
        },
        "Execute Put Lambda": {
            "Type": "Task",
            "InputPath": "$.MUST_COMPACTION.Output",
            "ResultPath": "$.putLambda",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_put_lambda",
            "Next": "Skip Put Lambda"
        },
        "Skip Put Lambda": {
            "Type": "Pass",
            "Next": "Skip Copy Mirror to Redshift Stage"
        },
        "Skip Copy Mirror to Redshift Stage": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.RS_COPY_INSERT",
                    "StringEquals": "Y",
                    "Next": "Redshift Copy Done"
                }
            ],
            "Default": "Copy Mirror to Redshift Stage"
        },
        "Copy Mirror to Redshift Stage": {
            "Type": "Task",
            "ResultPath": "$.Mirror to Redshift",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_redshift_copy",
                "Arguments": {
                    "--source_system": "MUST"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_RS_COPY_INSERT_FAILED"
                }
            ],
            "Next": "Redshift Copy Done"
        },
        "SNS_RS_COPY_INSERT_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS GLUE JOB FOR RS_COPY_INSERT FAILED !"
            },
            "End": false
        },
        "Redshift Copy Done": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MUST_SCHEDULE_SUCCESS"
        },
        "SNS_MUST_SCHEDULE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for MUST_COMPACTION Source completed successfully!!"
                }
            },
            "End": true
        }
    }
}
