{
  "StartAt": "StartParallel",
  "States": {
    "StartParallel": {
      "Type": "Parallel",
      "Next": "Completed",
      "ResultPath": "$.parallelResult",
      "Branches": [
        {
          "StartAt": "NONSS_SBOM_COMPACTION_GLPROD1",
          "States": {
            "NONSS_SBOM_COMPACTION_GLPROD1": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.NONSS_SBOM_COMPACTION_GLPROD1",
                  "StringEquals": "Y",
                  "Next": "PASS NONSS_SBOM_COMPACTION_GLPROD1"
                }
              ],
              "Default": "LOAD NONSS_SBOM_COMPACTION_GLPROD1"
            },
            "LOAD NONSS_SBOM_COMPACTION_GLPROD1": {
              "Type": "Task",
              "ResultPath": "$.NONSS_SBOM_COMPACTION_GLPROD1",
              "Resource": "arn:aws:states:::states:startExecution.sync",
              "Parameters": {
                "Input": {
                  "INPUT": {
                    "GET_LAMBDA": "N",
                    "SRC_NAME": "GLPROD",
                    "BATCH_NUMBER": "",
                    "job_lnd_mrr_incremental_refresh_ov_glprod_cst_cost_types_full": "N",
                    "job_lnd_mrr_incremental_refresh_ov_glprod_cst_item_costs_full": "N",
