{
  "StartAt": "START NONSS_SBOM_COMPACTION_GPM JOBS",
  "States": {
    "START NONSS_SBOM_COMPACTION_GPM JOBS": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$",
      "Next": "GET LAMBDA"
    },
    "GET LAMBDA": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.INPUT.GET_LAMBDA",
          "StringEquals": "Y",
          "Next": "SKIP GET LAMBDA"
        }
      ],
      "Default": "EXECUTE GET LAMBDA"
    },
    "EXECUTE GET LAMBDA": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.INPUT.BATCH_NUMBER",
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
      "Next": "SKIP GET LAMBDA"
    },
    "SKIP GET LAMBDA": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$.SKIP_LAMBDA",
      "Next": "StartParallel"
    },
    "StartParallel": {
      "Type": "Parallel",
      "Next": "Completed",
      "ResultPath": "$.parallelResult",
      "Branches": [
        {
          "StartAt": "GPM_BOM_BILL_OF_MATERIALS_FULL",
          "States": {
            "GPM_BOM_BILL_OF_MATERIALS_FULL": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_full_refresh_gpm_bom_bill_of_materials_full",
                  "StringEquals": "Y",
                  "Next": "PASS GPM_BOM_BILL_OF_MATERIALS_FULL"
                }
              ],
              "Default": "LOAD GPM_BOM_BILL_OF_MATERIALS_FULL"
            },
            "LOAD GPM_BOM_BILL_OF_MATERIALS_FULL": {
              "Type": "Task",
              "ResultPath": "$.GPM_BOM_BILL_OF_MATERIALS_FULL",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_full_refresh_gpm_bom_bill_of_materials_full",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_GPM_BOM_BILL_OF_MATERIALS_FULL_FAILED"
                }
              ],
              "Next": "PASS GPM_BOM_BILL_OF_MATERIALS_FULL"
            },
            "SNS_GPM_BOM_BILL_OF_MATERIALS_FULL_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for GPM_BOM_BILL_OF_MATERIALS_FULL Failed!"
              },
              "Next": "GPM_BOM_BILL_OF_MATERIALS_FULL_ACTIVITY"
            },
            "GPM_BOM_BILL_OF_MATERIALS_FULL_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_NONSS_GPM_BOM_BILL_OF_MATERIALS_FULL_ACTIVITY",
              "End": true
            },
            "PASS GPM_BOM_BILL_OF_MATERIALS_FULL": {
              "Type": "Pass",
              "End": true
            }
          }
        },
        {
          "StartAt": "GPM_BOM_INVENTORY_COMPONENTS_FULL",
          "States": {
            "GPM_BOM_INVENTORY_COMPONENTS_FULL": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_full_refresh_gpm_bom_inventory_components_full",
                  "StringEquals": "Y",
                  "Next": "PASS GPM_BOM_INVENTORY_COMPONENTS_FULL"
                }
              ],
              "Default": "LOAD GPM_BOM_INVENTORY_COMPONENTS_FULL"
            },
            "LOAD GPM_BOM_INVENTORY_COMPONENTS_FULL": {
              "Type": "Task",
              "ResultPath": "$.GPM_BOM_INVENTORY_COMPONENTS_FULL",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_full_refresh_gpm_bom_inventory_components_full",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_GPM_BOM_INVENTORY_COMPONENTS_FULL_FAILED"
                }
              ],
              "Next": "PASS GPM_BOM_INVENTORY_COMPONENTS_FULL"
            },
            "SNS_GPM_BOM_INVENTORY_COMPONENTS_FULL_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for GPM_BOM_INVENTORY_COMPONENTS_FULL Failed!"
              },
              "Next": "GPM_BOM_INVENTORY_COMPONENTS_FULL_ACTIVITY"
            },
            "GPM_BOM_INVENTORY_COMPONENTS_FULL_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_NONSS_GPM_BOM_INVENTORY_COMPONENTS_FULL_ACTIVITY",
              "End": true
            },
            "PASS GPM_BOM_INVENTORY_COMPONENTS_FULL": {
              "Type": "Pass",
              "End": true
            }
          }
        },
        {
          "StartAt": "GPM_EGO_ALL_ATTR_BASE_V_FULL",
          "States": {
            "GPM_EGO_ALL_ATTR_BASE_V_FULL": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_full_refresh_gpm_ego_all_attr_base_v_full",
                  "StringEquals": "Y",
                  "Next": "PASS GPM_EGO_ALL_ATTR_BASE_V_FULL"
                }
              ],
              "Default": "LOAD GPM_EGO_ALL_ATTR_BASE_V_FULL"
            },
            "LOAD GPM_EGO_ALL_ATTR_BASE_V_FULL": {
              "Type": "Task",
              "ResultPath": "$.GPM_EGO_ALL_ATTR_BASE_V_FULL",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_full_refresh_gpm_ego_all_attr_base_v_full",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_GPM_EGO_ALL_ATTR_BASE_V_FULL_FAILED"
                }
              ],
              "Next": "PASS GPM_EGO_ALL_ATTR_BASE_V_FULL"
            },
            "SNS_GPM_EGO_ALL_ATTR_BASE_V_FULL_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for GPM_EGO_ALL_ATTR_BASE_V_FULL Failed!"
              },
              "Next": "GPM_EGO_ALL_ATTR_BASE_V_FULL_ACTIVITY"
            },
            "GPM_EGO_ALL_ATTR_BASE_V_FULL_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_NONSS_GPM_EGO_ALL_ATTR_BASE_V_FULL_ACTIVITY",
              "End": true
            },
            "PASS GPM_EGO_ALL_ATTR_BASE_V_FULL": {
              "Type": "Pass",
              "End": true
            }
          }
        },
        {
          "StartAt": "GPM_MTL_SYSTEM_ITEMS_B_FULL",
          "States": {
            "GPM_MTL_SYSTEM_ITEMS_B_FULL": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.INPUT.job_lnd_mrr_incremental_refresh_ov_gpm_mtl_system_items_b_full",
                  "StringEquals": "Y",
                  "Next": "PASS GPM_MTL_SYSTEM_ITEMS_B_FULL"
                }
              ],
              "Default": "LOAD GPM_MTL_SYSTEM_ITEMS_B_FULL"
            },
            "LOAD GPM_MTL_SYSTEM_ITEMS_B_FULL": {
              "Type": "Task",
              "ResultPath": "$.GPM_MTL_SYSTEM_ITEMS_B_FULL",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_lnd_mrr_incremental_refresh_ov_gpm_mtl_system_items_b_full",
                "Arguments": {
                  "--batch_number.$": "$.INPUT.BATCH_NUMBER",
                  "--history_reload" : "Y"
                }
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_GPM_MTL_SYSTEM_ITEMS_B_FULL_FAILED"
                }
              ],
              "Next": "PASS GPM_MTL_SYSTEM_ITEMS_B_FULL"
            },
            "SNS_GPM_MTL_SYSTEM_ITEMS_B_FULL_FAILED": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for GPM_MTL_SYSTEM_ITEMS_B_FULL Failed!"
              },
              "Next": "GPM_MTL_SYSTEM_ITEMS_B_FULL_ACTIVITY"
            },
            "GPM_MTL_SYSTEM_ITEMS_B_FULL_ACTIVITY": {
              "Type": "Task",
              "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_NONSS_GPM_MTL_SYSTEM_ITEMS_B_FULL_ACTIVITY",
              "End": true
            },
            "PASS GPM_MTL_SYSTEM_ITEMS_B_FULL": {
              "Type": "Pass",
              "End": true
            }
          }
        }
      ]
    },
    "Completed": {
      "Type": "Pass",
      "InputPath": "$.SKIP_LAMBDA",
      "Next": "SNS_NONSS_SBOM_COMPACTION_GPM_SUCCESS"
    },
    "SNS_NONSS_SBOM_COMPACTION_GPM_SUCCESS": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.snsResult",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
        "Message": {
          "Subject": "AWS Step function for NONSS_SBOM_COMPACTION_GPM completed successfully!!"
        }
      },
      "End": true
    }
  }
}
