{
    "Comment": "Start daily compaction schedule @ 5AM IST",
    "StartAt": "ODP_DAILY_COMPACTION_SCHEDULE_START",
    "States": {
        "ODP_DAILY_COMPACTION_SCHEDULE_START": {
            "Type": "Pass",
            "Result": "$",
            "Next": "CHECK OTHER RUN"
        },
        "CHECK OTHER RUN": {
            "Type": "Task",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:mapp_daily_run_schedule",
            "Catch": [
                {
                    "ErrorEquals": [
                        "ZeroDivisionError"
                    ],
                    "Next": "SNS_DAILY_CHECK_FAILED"
                }
            ],
            "Next": "CONTINUE_DAILY"
        },
        "CONTINUE_DAILY": {
            "Type": "Pass",
            "Result": "$",
            "End": true
        },
        "SNS_DAILY_CHECK_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS STEP FUNCTION GLPROD CRAWLER FAILED !"
            },
            "Next": "CONTINUE_DAILY"
        }
    }
}
