{
    "Comment": "Operations data extraction process",
    "StartAt": "EXECUTE_OPS_EXTRACT",
    "States": {
        "EXECUTE_OPS_EXTRACT": {
            "Type": "Task",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "ResultPath": "$.OPS_EXTRACT_RESULT",
            "Parameters": {
                "JobName": "job_odp_load_status_extractor",
                "Arguments": {
                    "--max_threads": "16"
                }
            },
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "LOAD_STATUS_EXTRACTION_FAILURE"
                }
            ],
            "End": true
        },
        "LOAD_STATUS_EXTRACTION_FAILURE": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:LOAD_STATUS_EXTRACTION_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "ODP OPERATIONS DATA EXTRACT SFN FAILED"
            },
            "End": true
        }
    }
}
