{
  "StartAt": "START OUTLIERS_COMPACTION_CSO JOBS",
  "States": {
    "START OUTLIERS_COMPACTION_CSO JOBS": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$",
      "Next": "GET LAMBDA"
    },
    "GET LAMBDA": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.INPUT.GET_LAMBDA",
          "StringEquals": "Y",
          "Next": "SKIP GET LAMBDA"
        }
      ],
      "Default": "EXECUTE GET LAMBDA"
    },
    "EXECUTE GET LAMBDA": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.INPUT.BATCH_NUMBER",
      "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
      "Next": "SKIP GET LAMBDA"
    },
    "SKIP GET LAMBDA": {
      "Type": "Pass",
      "InputPath": "$",
      "ResultPath": "$.SKIP_LAMBDA",
      "Next": "CSO_CSO_CASE_DTL"
    },
    "CSO_CSO_CASE_DTL": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.INPUT.job_lnd_mrr_full_refresh_cso_cso_case_dtl",
          "StringEquals": "Y",
          "Next": "PASS CSO_CSO_CASE_DTL"
        }
      ],
      "Default": "LOAD CSO_CSO_CASE_DTL"
    },
    "LOAD CSO_CSO_CASE_DTL": {
      "Type": "Task",
      "ResultPath": "$.CSO_CSO_CASE_DTL",
      "Resource": "arn:aws:states:::glue:startJobRun.sync",
      "Parameters": {
        "JobName": "job_lnd_mrr_full_refresh_cso_cso_case_dtl",
        "Arguments": {
          "--batch_number.$": "$.INPUT.BATCH_NUMBER"
        }
      },
      "Retry": [
        {
          "ErrorEquals": [
            "States.Timeout"
          ],
          "MaxAttempts": 3,
          "IntervalSeconds": 60,
          "BackoffRate": 2
        }
      ],
      "Catch": [
        {
          "ErrorEquals": [
            "States.TaskFailed"
          ],
          "Next": "SNS_CSO_CSO_CASE_DTL_FAILED"
        }
      ],
      "Next": "PASS CSO_CSO_CASE_DTL"
    },
    "SNS_CSO_CSO_CASE_DTL_FAILED": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
        "Message.$": "$.Cause",
        "Subject": " AWS Glue job for SNS_CSO_CSO_CASE_DTL Failed!"
      },
      "Next": "CSO_CSO_CASE_DTL_ACTIVITY"
    },
    "CSO_CSO_CASE_DTL_ACTIVITY": {
      "Type": "Task",
      "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_CSO_CSO_CASE_DTL_ACTIVITY",
      "End": false
    },
    "PASS CSO_CSO_CASE_DTL": {
      "Type": "Pass",
      "Next": "Completed"
    },
    "Completed": {
      "Type": "Pass",
      "InputPath": "$.SKIP_LAMBDA",
      "Next": "SNS_OUTLIERS_COMPACTION_CSO_SUCCESS"
    },
    "SNS_OUTLIERS_COMPACTION_CSO_SUCCESS": {
      "Type": "Task",
      "InputPath": "$",
      "ResultPath": "$.snsResult",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
        "Message": {
          "Subject": "AWS Step function for OUTLIERS_COMPACTION_CSO completed successfully!!"
        }
      },
      "End": true
    }
  }
}
