{
    "StartAt": "START GLPROD COMPACTION JOBS",
    "States": {
        "START GLPROD COMPACTION JOBS": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$",
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$.SKIP_LAMBDA",
            "Next": "GLPROD_MTL_CATEGORY_SETS_B"
        },
        "GLPROD_MTL_CATEGORY_SETS_B": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_glprod_mtl_category_sets_b",
                    "StringEquals": "Y",
                    "Next": "PASS GLPROD_MTL_CATEGORY_SETS_B"
                }
            ],
            "Default": "LOAD GLPROD_MTL_CATEGORY_SETS_B"
        },
        "LOAD GLPROD_MTL_CATEGORY_SETS_B": {
            "Type": "Task",
            "ResultPath": "$.GLPROD1",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_lnd_mrr_incr_refresh_ov_glprod_mtl_category_sets_b",
                "Arguments": {
                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_GLPROD_MTL_CATEGORY_SETS_B_FAILED"
                }
            ],
            "Next": "PASS GLPROD_MTL_CATEGORY_SETS_B"
        },
        "SNS_GLPROD_MTL_CATEGORY_SETS_B_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for GLPROD_MTL_CATEGORY_SETS_B Failed!"
            },
            "Next": "GLPROD_MTL_CATEGORY_SETS_B_ACTIVITY"
        },
        "GLPROD_MTL_CATEGORY_SETS_B_ACTIVITY": {
            "Type": "Task",
            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_GLPROD_MTL_CATEGORY_SETS_B_ACTIVITY",
            "End": false
        },
        "PASS GLPROD_MTL_CATEGORY_SETS_B": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "InputPath": "$.SKIP_LAMBDA",
            "ResultPath": "$",
            "Next": "SNS_GLPROD_COMPACTION_SUCCESS"
        },
        "SNS_GLPROD_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for OUTLIERS_COMPACTION_GLPROD completed successfully!!"
                }
            },
            "End": true
        }
    }
}
