{
  "StartAt": "Start Glue Jobs",
  "States": {
    "Start Glue Jobs": {
      "Type": "Pass",
      "InputPath": "$",
      "OutputPath": "$",
      "Next": "Lambda"
    },
    "Lambda": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.SKIP_FLAG.getJobDtls_sfn",
          "StringEquals": "Y",
          "Next": "Skip Lambda"
        }
      ],
      "Default": "Execute Lambda"
    },
    "Execute Lambda": {
      "Type": "Task",
      "InputPath": "$",
      "OutputPath": "$",
      "ResultPath": "$.SKIP_FLAG",
      "Resource": "arn:aws:lambda:us-east-1:541574621075:function:getJobDtls_sfn",
      "Next": "Skip Lambda"
    },
    "Skip Lambda": {
      "Type": "Pass",
      "InputPath": "$",
      "OutputPath": "$",
      "Next": "StartParallel"
    },
