              "Default": "Load SIT_Prd_Main_d"
            },
            "Load SIT_Prd_Main_d": {
              "Type": "Task",
              "Resource": "arn:aws:states:::glue:startJobRun.sync",
              "Parameters": {
                "JobName": "job_mrr_fnd_prd_main_d_sit"
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.Timeout"
                  ],
                  "MaxAttempts": 3,
                  "IntervalSeconds": 60,
                  "BackoffRate": 2
                }
              ],
              "Catch": [
                {
                  "ErrorEquals": [
                    "States.TaskFailed"
                  ],
                  "Next": "SNS_SIT_Prd_Main_d_Failed"
                }
              ],
              "Next": "Pass SIT_Prd_Main_d"
            },
            "SNS_SIT_Prd_Main_d_Failed": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sns:publish",
              "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:PROD_ODP_SF_NOTIFY",
                "Message.$": "$.Cause",
                "Subject": " AWS Glue job for Prd_Main_d-SIT Failed!"
              },
              "End": false
            },
