{
    "StartAt": "START SR_CDS_DATA_QUALITY SCHEDULE",
    "States": {
        "START SR_CDS_DATA_QUALITY SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "DATA_QUALITY"
        },
        "DATA_QUALITY": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.DATA_QUALITY",
                    "StringEquals": "Y",
                    "Next": "SKIP DATA_QUALITY"
                }
            ],
            "Default": "LOAD DATA_QUALITY"
        },
        "LOAD DATA_QUALITY": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.DATA_QUALITY",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "Data_Quality"
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_DATA_QUALITY_FAILED"
                }
            ],
            "Next": "SKIP DATA_QUALITY"
        },
        "SNS_DATA_QUALITY_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR DATA_QUALITY FAILED!"
            },
            "End": false
        },
        "SKIP DATA_QUALITY": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_SR_CDS_DATA_QUALITY_SUCCESS"
        },
        "SNS_SR_CDS_DATA_QUALITY_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SR_CDS_DATA_QUALITY completed successfully!!"
                }
            },
            "End": true
        }
    }
}
