{
    "StartAt": "START GLPROD COMPACTION JOBS",
    "States": {
        "START GLPROD COMPACTION JOBS": {
            "Type": "Wait",
            "InputPath": "$",
            "Seconds": 75,
            "Next": "GET LAMBDA"
        },
        "GET LAMBDA": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.GET_LAMBDA",
                    "StringEquals": "Y",
                    "Next": "SKIP GET LAMBDA"
                }
            ],
            "Default": "EXECUTE GET LAMBDA"
        },
        "EXECUTE GET LAMBDA": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.INPUT.BATCH_NUMBER",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:ddb_get_lambda",
            "Next": "SKIP GET LAMBDA"
        },
        "SKIP GET LAMBDA": {
            "Type": "Pass",
            "InputPath": "$",
            "ResultPath": "$.SKIP_LAMBDA",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "ResultPath": "$.parallelResult",
            "Branches": [
                {
                    "StartAt": "GLPROD_GEMS_ITEM_TB",
                    "States": {
                        "GLPROD_GEMS_ITEM_TB": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_glprod_gems_item_tb",
                                    "StringEquals": "Y",
                                    "Next": "PASS GLPROD_GEMS_ITEM_TB"
                                }
                            ],
                            "Default": "LOAD GLPROD_GEMS_ITEM_TB"
                        },
                        "LOAD GLPROD_GEMS_ITEM_TB": {
                            "Type": "Task",
                            "ResultPath": "$.GLPROD_GEMS_ITEM_TB",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_incr_refresh_ov_glprod_gems_item_tb",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_GLPROD_GEMS_ITEM_TB_FAILED"
                                }
                            ],
                            "Next": "PASS GLPROD_GEMS_ITEM_TB"
                        },
                        "SNS_GLPROD_GEMS_ITEM_TB_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SNS_GLPROD_GEMS_ITEM_TB Failed!"
                            },
                            "Next": "GLPROD_GEMS_ITEM_TB_ACTIVITY"
                        },
                        "GLPROD_GEMS_ITEM_TB_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_GLPROD_GEMS_ITEM_TB_ACTIVITY",
                            "End": true
                        },
                        "PASS GLPROD_GEMS_ITEM_TB": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "GLPROD_GEMS_COST_TB",
                    "States": {
                        "GLPROD_GEMS_COST_TB": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_glprod_gems_cost_tb",
                                    "StringEquals": "Y",
                                    "Next": "PASS GLPROD_GEMS_COST_TB"
                                }
                            ],
                            "Default": "LOAD GLPROD_GEMS_COST_TB"
                        },
                        "LOAD GLPROD_GEMS_COST_TB": {
                            "Type": "Task",
                            "ResultPath": "$.GLPROD_GEMS_COST_TB",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_incr_refresh_ov_glprod_gems_cost_tb",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_GLPROD_GEMS_COST_TB_FAILED"
                                }
                            ],
                            "Next": "PASS GLPROD_GEMS_COST_TB"
                        },
                        "SNS_GLPROD_GEMS_COST_TB_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SNS_GLPROD_GEMS_COST_TB Failed!"
                            },
                            "Next": "GLPROD_GEMS_COST_TB_ACTIVITY"
                        },
                        "GLPROD_GEMS_COST_TB_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_GLPROD_GEMS_COST_TB_ACTIVITY",
                            "End": true
                        },
                        "PASS GLPROD_GEMS_COST_TB": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "GLPROD_GEMS_GPO_RETURN_RECS_TBL",
                    "States": {
                        "GLPROD_GEMS_GPO_RETURN_RECS_TBL": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_glprod_gems_gpo_return_recs_tbl",
                                    "StringEquals": "Y",
                                    "Next": "PASS GLPROD_GEMS_GPO_RETURN_RECS_TBL"
                                }
                            ],
                            "Default": "LOAD GLPROD_GEMS_GPO_RETURN_RECS_TBL"
                        },
                        "LOAD GLPROD_GEMS_GPO_RETURN_RECS_TBL": {
                            "Type": "Task",
                            "ResultPath": "$.GLPROD_GEMS_GPO_RETURN_RECS_TBL",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_incr_refresh_ov_glprod_gems_gpo_return_recs_tbl",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_GLPROD_GEMS_GPO_RETURN_RECS_TBL_FAILED"
                                }
                            ],
                            "Next": "PASS GLPROD_GEMS_GPO_RETURN_RECS_TBL"
                        },
                        "SNS_GLPROD_GEMS_GPO_RETURN_RECS_TBL_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SNS_GLPROD_GEMS_GPO_RETURN_RECS_TBL Failed!"
                            },
                            "Next": "GLPROD_GEMS_GPO_RETURN_RECS_TBL_ACTIVITY"
                        },
                        "GLPROD_GEMS_GPO_RETURN_RECS_TBL_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_GLPROD_GEMS_GPO_RETURN_RECS_TBL_ACTIVITY",
                            "End": true
                        },
                        "PASS GLPROD_GEMS_GPO_RETURN_RECS_TBL": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "GLPROD_MTL_TRANSACTION_REASONS",
                    "States": {
                        "GLPROD_MTL_TRANSACTION_REASONS": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.INPUT.job_lnd_mrr_incr_refresh_ov_glprod_mtl_transaction_reasons",
                                    "StringEquals": "Y",
                                    "Next": "PASS GLPROD_MTL_TRANSACTION_REASONS"
                                }
                            ],
                            "Default": "LOAD GLPROD_MTL_TRANSACTION_REASONS"
                        },
                        "LOAD GLPROD_MTL_TRANSACTION_REASONS": {
                            "Type": "Task",
                            "ResultPath": "$.GLPROD_MTL_TRANSACTION_REASONS",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_lnd_mrr_incr_refresh_ov_glprod_mtl_transaction_reasons",
                                "Arguments": {
                                    "--batch_number.$": "$.INPUT.BATCH_NUMBER"
                                }
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_GLPROD_MTL_TRANSACTION_REASONS_FAILED"
                                }
                            ],
                            "Next": "PASS GLPROD_MTL_TRANSACTION_REASONS"
                        },
                        "SNS_GLPROD_MTL_TRANSACTION_REASONS_FAILED": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for SNS_GLPROD_MTL_TRANSACTION_REASONS Failed!"
                            },
                            "Next": "GLPROD_MTL_TRANSACTION_REASONS_ACTIVITY"
                        },
                        "GLPROD_MTL_TRANSACTION_REASONS_ACTIVITY": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:us-east-1:245792935030:activity:COMPACTION_GLPROD_MTL_TRANSACTION_REASONS_ACTIVITY",
                            "End": true
                        },
                        "PASS GLPROD_MTL_TRANSACTION_REASONS": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "InputPath": "$.SKIP_LAMBDA",
            "Next": "SNS_GLPROD_COMPACTION_SUCCESS"
        },
        "SNS_GLPROD_COMPACTION_SUCCESS": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.snsResult",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for GLPROD COMPACTION completed successfully!!"
                }
            },
            "End": true
        }
    }
}
