{
    "StartAt": "START SR_ENTITY_BFE SCHEDULE",
    "States": {
        "START SR_ENTITY_BFE SCHEDULE": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "BFE_INVOICE_TIME_TRACKER_DETAIL"
        },
        "BFE_INVOICE_TIME_TRACKER_DETAIL": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.BFE_INVOICE_TIME_TRACKER_DETAIL",
                    "StringEquals": "Y",
                    "Next": "PASS BFE_INVOICE_TIME_TRACKER_DETAIL"
                }
            ],
            "Default": "LOAD BFE_INVOICE_TIME_TRACKER_DETAIL"
        },
        "LOAD BFE_INVOICE_TIME_TRACKER_DETAIL": {
            "Type": "Task",
            "InputPath": "$",
            "ResultPath": "$.BFE_INVOICE_TIME_TRACKER_DETAIL",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "job_mrr_entity_cds_rs_proc",
                "Arguments": {
                    "--rs_proc_name": "hdl_services_sp.sp_ar_invc_tim_trk_det_fs_bfe()"
                }
            },
            "Retry": [
                {
                    "ErrorEquals": [
                        "States.Timeout"
                    ],
                    "MaxAttempts": 3,
                    "IntervalSeconds": 60,
                    "BackoffRate": 2
                }
            ],
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.TaskFailed"
                    ],
                    "Next": "SNS_BFE_INVOICE_TIME_TRACKER_DETAIL_FAILED"
                }
            ],
            "Next": "PASS BFE_INVOICE_TIME_TRACKER_DETAIL"
        },
        "SNS_BFE_INVOICE_TIME_TRACKER_DETAIL_FAILED": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_FAILURE",
                "Message.$": "$.Cause",
                "Subject": "AWS GLUE SHELL JOB FOR BFE_INVOICE_TIME_TRACKER_DETAIL FAILED!"
            },
            "End": false
        },
        "PASS BFE_INVOICE_TIME_TRACKER_DETAIL": {
            "Type": "Pass",
            "Next": "Completed"
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_SR_ENTITY_BFE_SUCCESS"
        },
        "SNS_SR_ENTITY_BFE_SUCCESS": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:ODP_SFN_SUCCESS",
                "Message": {
                    "Subject": "AWS Step function for SR_ENTITY_BFE completed successfully!!!!"
                }
            },
            "End": true
        }
    }
}
