{
    "Comment": "ci",
    "StartAt": "START ci extracts",
    "States": {
        "START ci extracts": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "CHECK transaction_summary_report"
        },
        "CHECK transaction_summary_report": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.INPUT.transaction_summary_report",
                    "StringEquals": "Y",
                    "Next": "SKIP transaction_summary_report"
                }
            ],
            "Default": "EXTRACT transaction_summary_report"
        },
        "EXTRACT transaction_summary_report": {
            "Type": "Task",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "us-prod-daasbatchextract",
                "Arguments": {
                    "--application_name": "ci",
                    "--extract_number": "1"
                }
            },
            "End": true
        },
        "SKIP transaction_summary_report": {
            "Type": "Pass",
            "End": true
        }
    }
}
