{
    "Comment": "ecmpr",
    "StartAt": "START ecmpr extracts",
    "States": {
        "START ecmpr extracts": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "CHECK Exportcontactcustomers"
        },
        "CHECK Exportcontactcustomers": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.SFN_INPUT.Exportcontactcustomers",
                    "StringEquals": "Y",
                    "Next": "SKIP Exportcontactcustomers"
                }
            ],
            "Default": "EXTRACT Exportcontactcustomers"
        },
        "EXTRACT Exportcontactcustomers": {
            "Type": "Task",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "us-prod-daasbatchextract",
                "Arguments": {
                    "--application_name": "ecmpr",
                    "--extract_number": "1"
                }
            },
            "ResultPath": null,
            "Catch": [
                {
                    "ErrorEquals": [
                        "States.ALL"
                    ],
                    "ResultPath": null,
                    "Next": "CHECK ExporteCommRecommendations"
                }
            ],
            "Next": "CHECK ExporteCommRecommendations"
        },
        "SKIP Exportcontactcustomers": {
            "Type": "Pass",
            "Next": "CHECK ExporteCommRecommendations"
        },
        "CHECK ExporteCommRecommendations": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.SFN_INPUT.ExporteCommRecommendations",
                    "StringEquals": "Y",
                    "Next": "SKIP ExporteCommRecommendations"
                }
            ],
            "Default": "EXTRACT ExporteCommRecommendations"
        },
        "EXTRACT ExporteCommRecommendations": {
            "Type": "Task",
            "Resource": "arn:aws:states:::glue:startJobRun.sync",
            "Parameters": {
                "JobName": "us-prod-daasbatchextract",
                "Arguments": {
                    "--application_name": "ecmpr",
                    "--extract_number": "2"
                }
            },
            "ResultPath": null,
            "End": true
        },
        "SKIP ExporteCommRecommendations": {
            "Type": "Pass",
            "End": true
        }
    }
}
