{
    "StartAt": "Start Glue Jobs",
    "States": {
        "Start Glue Jobs": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Lambda"
        },
        "Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.SKIP_FLAG.getJobDtls_sfn",
                    "StringEquals": "Y",
                    "Next": "Skip Lambda"
                }
            ],
            "Default": "Execute Lambda"
        },
        "Execute Lambda": {
            "Type": "Task",
            "InputPath": "$",
            "OutputPath": "$",
            "ResultPath": "$.SKIP_FLAG",
            "Resource": "arn:aws:lambda:us-east-1:245792935030:function:getJobDtls_sfn",
            "Next": "Skip Lambda"
        },
        "Skip Lambda": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "Branches": [
                {
                    "StartAt": "HR_Geo_addr_d",
                    "States": {
                        "HR_Geo_addr_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_geo_addr_d_hr_flat_file",
                                    "StringEquals": "Y",
                                    "Next": "Pass HR_Geo_addr_d"
                                }
                            ],
                            "Default": "Load HR_Geo_addr_d"
                        },
                        "Load HR_Geo_addr_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_geo_addr_d_hr_flat_file"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_HR_Geo_addr_d_Failed"
                                }
                            ],
                            "Next": "Pass HR_Geo_addr_d"
                        },
                        "SNS_HR_Geo_addr_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:PROD_ODP_SF_NOTIFY",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for Geo_addr_d-HR Failed!"
                            },
                            "End": false
                        },
                        "Pass HR_Geo_addr_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "HR_Prt_persn_d",
                    "States": {
                        "HR_Prt_persn_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_prt_persn_d_sit",
                                    "StringEquals": "Y",
                                    "Next": "Pass HR_Prt_persn_d"
                                }
                            ],
                            "Default": "Load HR_Prt_persn_d"
                        },
                        "Load HR_Prt_persn_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_prt_persn_d_Sit"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_HR_Prt_persn_d_Failed"
                                }
                            ],
                            "Next": "Pass HR_Prt_persn_d"
                        },
                        "SNS_HR_Prt_persn_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:245792935030:PROD_ODP_SF_NOTIFY",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for Prt_cust_d-SIT Failed!"
                            },
                            "End": false
                        },
                        "Pass HR_Prt_persn_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_HR_MRR_FND_Success"
        },
        "SNS_HR_MRR_FND_Success": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:245792935030:PROD_ODP_SF_NOTIFY",
                "Message": {
                    "Subject": "AWS Step function for HR completed successfully!!"
                }
            },
            "End": true
        }
    }
}
