{
    "StartAt": "Start Glue Jobs",
    "States": {
        "Start Glue Jobs": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "Lambda"
        },
        "Lambda": {
            "Type": "Choice",
            "Choices": [
                {
                    "Variable": "$.SKIP_FLAG.getJobDtls_sfn",
                    "StringEquals": "Y",
                    "Next": "Skip Lambda"
                }
            ],
            "Default": "Execute Lambda"
        },
        "Execute Lambda": {
            "Type": "Task",
            "InputPath": "$",
            "OutputPath": "$",
            "ResultPath": "$.SKIP_FLAG",
            "Resource": "arn:aws:lambda:us-east-1:541574621075:function:getJobDtls_sfn",
            "Next": "Skip Lambda"
        },
        "Skip Lambda": {
            "Type": "Pass",
            "InputPath": "$",
            "OutputPath": "$",
            "Next": "StartParallel"
        },
        "StartParallel": {
            "Type": "Parallel",
            "Next": "Completed",
            "Branches": [
                {
                    "StartAt": "MUST_Prd_Main_d",
                    "States": {
                        "MUST_Prd_Main_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_prd_main_d_must",
                                    "StringEquals": "Y",
                                    "Next": "Pass MUST_Prd_Main_d"
                                }
                            ],
                            "Default": "Load MUST_Prd_Main_d"
                        },
                        "Load MUST_Prd_Main_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_prd_main_d_must"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MUST_Prd_Main_d_Failed"
                                }
                            ],
                            "Next": "Pass MUST_Prd_Main_d"
                        },
                        "SNS_MUST_Prd_Main_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:541574621075:ODP_SF_Success",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for Prd_Main_d-MUST Failed!"
                            },
                            "End": false
                        },
                        "Pass MUST_Prd_Main_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "MUST_Geo_addr_d",
                    "States": {
                        "MUST_Geo_addr_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_geo_addr_d_must",
                                    "StringEquals": "Y",
                                    "Next": "Pass MUST_Geo_addr_d"
                                }
                            ],
                            "Default": "Load MUST_Geo_addr_d"
                        },
                        "Load MUST_Geo_addr_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_geo_addr_d_must"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MUST_Geo_addr_d_Failed"
                                }
                            ],
                            "Next": "Pass MUST_Geo_addr_d"
                        },
                        "SNS_MUST_Geo_addr_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:541574621075:ODP_SF_Success",
                                "Message.$": "$.Cause",
                                "Subject": "AWS Glue job for Geo_addr_d-MUST Failed!"
                            },
                            "End": false
                        },
                        "Pass MUST_Geo_addr_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "MUST_Prt_Cust_d",
                    "States": {
                        "MUST_Prt_Cust_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_prt_cust_d_must",
                                    "StringEquals": "Y",
                                    "Next": "Pass MUST_Prt_Cust_d"
                                }
                            ],
                            "Default": "Load MUST_Prt_Cust_d"
                        },
                        "Load MUST_Prt_Cust_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_prt_cust_d_must"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MUST_Prt_Cust_d_Failed"
                                }
                            ],
                            "Next": "Pass MUST_Prt_Cust_d"
                        },
                        "SNS_MUST_Prt_Cust_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:541574621075:ODP_SF_Success",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for Prt_Cust_d-MUST Failed!"
                            },
                            "End": true
                        },
                        "Pass MUST_Prt_Cust_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                },
                {
                    "StartAt": "MUST_Prt_persn_d",
                    "States": {
                        "MUST_Prt_persn_d": {
                            "Type": "Choice",
                            "Choices": [
                                {
                                    "Variable": "$.SKIP_FLAG.job_mrr_fnd_prt_persn_d_must",
                                    "StringEquals": "Y",
                                    "Next": "Pass MUST_Prt_persn_d"
                                }
                            ],
                            "Default": "Load MUST_Prt_persn_d"
                        },
                        "Load MUST_Prt_persn_d": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::glue:startJobRun.sync",
                            "Parameters": {
                                "JobName": "job_mrr_fnd_prt_persn_d_must"
                            },
                            "Retry": [
                                {
                                    "ErrorEquals": [
                                        "States.Timeout"
                                    ],
                                    "MaxAttempts": 3,
                                    "IntervalSeconds": 60,
                                    "BackoffRate": 2
                                }
                            ],
                            "Catch": [
                                {
                                    "ErrorEquals": [
                                        "States.TaskFailed"
                                    ],
                                    "Next": "SNS_MUST_Prt_persn_d_Failed"
                                }
                            ],
                            "Next": "Pass MUST_Prt_persn_d"
                        },
                        "SNS_MUST_Prt_persn_d_Failed": {
                            "Type": "Task",
                            "Resource": "arn:aws:states:::sns:publish",
                            "Parameters": {
                                "TopicArn": "arn:aws:sns:us-east-1:541574621075:ODP_SF_Success",
                                "Message.$": "$.Cause",
                                "Subject": " AWS Glue job for Prt_cust_d-MUST Failed!"
                            },
                            "End": false
                        },
                        "Pass MUST_Prt_persn_d": {
                            "Type": "Pass",
                            "End": true
                        }
                    }
                }
            ]
        },
        "Completed": {
            "Type": "Pass",
            "Next": "SNS_MUST_MRR_FND_Success"
        },
        "SNS_MUST_MRR_FND_Success": {
            "Type": "Task",
            "Resource": "arn:aws:states:::sns:publish",
            "Parameters": {
                "TopicArn": "arn:aws:sns:us-east-1:541574621075:ODP_SF_Success",
                "Message": {
                    "Subject": "AWS Step function for MUST completed successfully!!"
                }
            },
            "End": true
        }
    }
}
